INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (1, 'Mare', 'Mare means ''sea'' and refers to a large body of salt water that is surrounded in whole or in part by land.', 'MAH-reh', 'From Proto-Indo-European *móri, related to Latin ''Maria'', meaning large body of water.', 'The sailors navigated across the vast sea.', 'Nautae trans magnum mare navigaverunt.', 'The sea was calm and serene at dawn.', 'Mare tranquillum et serenum aurora erat.', 'She gazed at the endless horizon of the sea.', 'Aspiciebat ad infinitum horizontem maris.', '{Oceanus,Pelagus}', '{}', 'mare.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (2, 'Amor', 'Amor means ''love'', describing a profound feeling of affection and attachment.', 'AH-mor', 'From Latin ''amare'', meaning to love or to hold dear.', 'The poet wrote about eternal love.', 'Poeta de aeterno amore scripsit.', 'Her love for music is evident in everything she does.', 'Amor eius ad musicam patet in omnibus quae facit.', 'They showed their love through kindness and understanding.', 'Amorem suum per benignitatem et intellectum ostenderunt.', '{Caritas,Dilectio}', '{Odio}', 'amor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (3, 'Aqua', 'Water, the basic necessity of life.', 'AH-kwah', 'From Proto-Italic *akwā, derived from Proto-Indo-European *h₂ekweh₂.', 'The clear water fills the cup.', 'Aqua clara poculum implet.', 'A river flows with fresh water.', 'Fluvius aqua recenti fluit.', 'They clean the floor with water.', 'Pavimentum aqua purgant.', '{Unda,Liquor}', '{}', 'aqua.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (4, 'Pax', 'Peace, a state of tranquility or quiet.', 'PAHKS', 'From Proto-Italic *paktis, from Proto-Indo-European *peḱ- (''to agree'').', 'The nations seek peace.', 'Gentes pacem quaerunt.', 'Peace reigns in the city.', 'Pax in urbe regnat.', 'His heart is filled with peace.', 'Cor eius pace plenus est.', '{Quies,Tranquillitas}', '{Bellum}', 'pax.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (5, 'Rex', 'King, the male ruler of an independent state.', 'REHKS', 'From Proto-Italic *rēks, from Proto-Indo-European *h₃rḗǵs (''ruler'').', 'The king leads his people.', 'Rex populum suum ducit.', 'A wise king is beloved.', 'Rex sapiens amatur.', 'The king holds court.', 'Rex curiam tenet.', '{Monarch,Regis}', '{Servus}', 'rex.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (178, 'Domus', 'Domus means ''house'' or ''home'', referring to a place where people reside.', 'DOH-moos', 'From Proto-Indo-European *dṓm, meaning ''house''.', 'She returned to her house after a long journey.', NULL, 'The house was large and beautiful.', NULL, 'They gathered at his house for a meeting.', NULL, '{Aedes,Tectum}', '{}', 'domus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (7, 'Arbor', 'Tree, a perennial plant with an elongated stem or trunk.', 'AR-bor', 'From Proto-Italic *arɸōs, from Proto-Indo-European *h₂erdʰ- (''to grow'').', 'The tree''s shade is refreshing.', 'Umbra arboris refrigerat.', 'Birds nest in the tree.', 'Aves in arbore nidificant.', 'The tree grows strong and tall.', 'Arbor fortis et alta crescit.', '{Silva,Frutex}', '{}', 'arbor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (8, 'Terra', 'Earth, the land surface of the world.', 'TEHR-ra', 'From Proto-Italic *tersā, from Proto-Indo-European *ters- (''dry'').', 'They cultivate the earth.', 'Terram colunt.', 'The earth is fertile and green.', 'Terra fertilis et viridis est.', 'He marks his territory.', 'Territorium suum notat.', '{Humus,Ager}', '{}', 'terra.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (179, 'Lapis', 'Lapis means ''stone'', referring to a naturally occurring solid aggregate of minerals.', 'LAH-pis', 'Derived from the Proto-Indo-European *leh₁p-, meaning ''stone'' or ''to peel''.', 'He picked up a stone from the ground.', NULL, 'The ancient wall was made of stone.', NULL, 'Stones lined the path through the garden.', NULL, '{Saxum,Petra}', '{}', 'lapis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (10, 'Fortuna', 'Fortune, chance or luck regarded as an external arbitrary force.', 'for-TOO-nah', 'From Proto-Italic *fortonā, possibly from Proto-Indo-European *bher- (''to carry'').', 'Fortune favors the brave.', 'Fortuna fortes adiuvat.', 'He takes a risk for fortune.', 'Periculum pro fortuna capiet.', 'Her fortune has changed suddenly.', 'Fortuna eius subito mutata est.', '{Sors,Casus}', '{}', 'fortuna.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (11, 'Insula', 'Island, a tract of land surrounded by water.', 'IN-su-lah', 'From Proto-Italic *enisalā, likely from Proto-Indo-European *eynsula (''island'').', 'The island is lush and green.', 'Insula virens et florida est.', 'They sailed to a distant island.', 'Ad insulam longinquam navigaverunt.', 'The island is surrounded by cliffs.', 'Insula rupibus circumdata est.', '{Aegyptus,Naso}', '{}', 'insula.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (12, 'Stella', 'Star, a luminous point in the night sky.', 'STEL-lah', 'From Proto-Italic *stelhā, from Proto-Indo-European *h₂stḗr (''star'').', 'Stars shine brightly at night.', 'Stellae noctu lucent.', 'The star guides the sailors.', 'Stella nautas ducit.', 'She wishes upon a falling star.', 'Super stellam cadentem votum facit.', '{Astrum,Sidus}', '{}', 'stella.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (13, 'Ignis', 'Fire, the phenomenon of combustion manifested in light, flame, and heat.', 'IG-nis', 'From Proto-Italic *ignis, from Proto-Indo-European *h₁n̥gʷnís (''fire'').', 'Fire warms the hall.', 'Ignis atrium calefacit.', 'The fire blazes brightly.', 'Ignis lucidus ardent.', 'They gathered around the fire.', 'Circa ignem convenerunt.', '{Flamma,Focus}', '{Aqua}', 'ignis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (14, 'Lux', 'Light, the natural agent that stimulates sight and makes things visible.', 'LUKS', 'From Proto-Italic *louks, from Proto-Indo-European *lówkʷs (''light'').', 'Light illuminates the room.', 'Lux conclave illuminat.', 'The dawn brings light.', 'Aurora lucem affert.', 'They follow the light of the lantern.', 'Lucem lanternae sequuntur.', '{Lumen,Clarus}', '{Tenebrae}', 'lux.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (180, 'Campus', 'Campus means ''field'', usually referring to an open space used for agriculture or as a place of activity.', 'KAM-poos', 'Likely from Proto-Indo-European *kamp- meaning ''to bend'' or ''crooked''.', 'The farmer worked in the field from dawn till dusk.', NULL, 'The field was covered with wildflowers.', NULL, 'They played soccer in the open field.', NULL, '{Ager,Arvum}', '{}', 'campus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (16, 'Vitrum', 'Glass, a hard, brittle substance typically transparent or translucent.', 'VIH-trum', 'From Latin vitrum, of uncertain origin but possibly related to vītus (''life'').', 'The glass was filled with water.', 'Vitrum aqua impletum erat.', 'He looked through the clear glass.', 'Per vitrum clarum aspexit.', 'Glass can shatter easily.', 'Vitrum facile frangi potest.', '{Crystallum,Speculum}', '{}', 'vitrum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (17, 'Fluctus', 'Wave, a long body of water curling into an arched form and breaking on the shore.', 'FLUK-tus', 'From Latin, based on fluo (''flow'').', 'Waves crash on the beach.', 'Fluctus in litus impingunt.', 'The waves are strong today.', 'Fluctus hodie fortes sunt.', 'He watched the waves from the shore.', 'A litore fluctus spectabat.', '{Unda,Mari}', '{}', 'fluctus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (18, 'Silva', 'Forest, a large area covered chiefly with trees and undergrowth.', 'SIL-wah', 'From Proto-Italic *silwā, from Proto-Indo-European *selu- (''wood, forest'').', 'The forest is dense and mysterious.', 'Silva densa et mystica est.', 'Animals thrive in the forest.', 'Animalia in silva florent.', 'She wandered through the forest.', 'Per silvam erravit.', '{Nemus,Lucus}', '{}', 'silva.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (181, 'Civitas', 'Civitas means ''state'' or ''citizenship'', often referring to a community or body of citizens.', 'KEE-wee-tahs', 'From Proto-Indo-European *ḱey-, meaning ''to lie down'' or ''settle''.', 'The state provides services to its citizens.', NULL, 'He was proud of his Roman citizenship.', NULL, 'The laws of the state must be followed.', NULL, '{Respublica,Urbs}', '{}', 'civitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (19, 'Aurora', 'Dawn, the first light of day.', 'ow-ROH-rah', 'From Latin aurora (''dawn''), associated with the goddess of dawn.', 'The dawn heralds a new day.', 'Aurora novum diem praenunciat.', 'Aurora tinges the sky with colors.', 'Aurora caelum coloribus tingit.', 'He rose with the dawn.', 'Cum aurora surrexit.', '{Diluculum,Ortus}', '{}', 'aurora.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (20, 'Gladius', 'Sword, a weapon with a long blade for cutting or thrusting.', 'GLAH-dee-us', 'From Proto-Italic *gladios, possibly from Proto-Indo-European *gʷlh₂tós (''stab, cut'').', 'The sword glints in the sunlight.', 'Gladius in sole micat.', 'He clenched the sword tightly.', 'Gladium strenue tenuit.', 'The sword was passed down through generations.', 'Gladius generibus traditus est.', '{Spatha,Ensis}', '{}', 'gladius.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (21, 'Uxor', 'Wife, a married woman considered in relation to her spouse.', 'OOK-sore', 'From Proto-Italic *ukᶅisōr, from Proto-Indo-European *ukʷs- (''female'').', 'The wife supported her husband.', 'Uxor maritum adiuvit.', 'She attends the gathering with her wife.', 'Cum uxore convivium frequentat.', 'He spoke fondly of his wife.', 'De uxore sua amanter locutus est.', '{Coniunx,Mulier}', '{}', 'uxor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (22, 'Vita', 'Life, the existence of an individual human being or animal.', 'WIH-tah', 'From Proto-Italic *wītā, from Proto-Indo-European *gʷih₃wós (''alive'').', 'Life is full of adventures.', 'Vita plena est adventuris.', 'She cherishes every moment of her life.', 'Omne momentum vitae suae colit.', 'He leads a simple life.', 'Vitam simplicem ducit.', '{Anima,Existential}', '{Mors}', 'vita.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (23, 'Tempus', 'Time, the indefinite continued progress of existence.', 'TEM-pus', 'From Proto-Italic *tempos, derived from Proto-Indo-European *tem- (''to cut'').', 'Time waits for no one.', 'Tempus neminem manet.', 'They marked the passage of time.', 'Transitum temporis notaverunt.', 'Time moves slowly in peaceful moments.', 'In momentis pacificis tempus lente movetur.', '{Hora,Aetas}', '{}', 'tempus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (182, 'Opus', 'Opus means ''work'', typically referring to a piece of art or a large and important accomplishment.', 'OH-poos', 'From Proto-Indo-European *h₃ep-, meaning ''to work'' or ''perform''.', 'The artist completed his greatest work.', NULL, 'Building the bridge was an arduous work.', NULL, 'Each opus added to his growing reputation.', NULL, '{Labor,Operatio}', '{}', 'opus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (25, 'Silentium', 'Silence, the absence of sound.', 'si-len-TEE-um', 'From Latin silentium.', 'The forest was enveloped in silence.', 'Sylva silentio obtecta erat.', 'Silence fell over the room.', 'Silentium super conclave cecidit.', 'He appreciated the silence of the early morning.', 'Silentium primae matutinum aestimavit.', '{Taciturnitas,Quies}', '{Clamor}', 'silentium.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (26, 'Flumen', 'River, a large natural stream of water flowing in a channel to the sea, a lake, or another river.', 'FLOO-men', 'From Latin flūmen, derived from fluō (''flow'').', 'The river flows gently through the valley.', 'Flumen leniter per vallem fluat.', 'He sat by the river and watched the water.', 'Apud flumen sedit et aquam spectavit.', 'Rivers are important for transportation.', 'Flumina sunt ad vecturas magni momenti.', '{Amnis,Rivus}', '{}', 'flumen.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (27, 'Spes', 'Hope, a feeling of expectation and desire for a particular thing to happen.', 'SPAYS', 'From Proto-Italic *speh-, from Proto-Indo-European *spek- (''to look, to observe'').', 'Hope springs eternal in the human breast.', 'Spes aeterna in pectore humano oritur.', 'They have hope for a better future.', 'Spem in melior futura habent.', 'Hope gave them strength to continue.', 'Spes eis vim ad pergendum dedit.', '{Expectatio,Confidentia}', '{Desperatio}', 'spes.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (28, 'Caelum', 'Sky, the expanse of air over any given point on the earth.', 'KAI-lum', 'From Proto-Italic *kaiwelo-, from Proto-Indo-European *ḱaywelo- (''the act of carving, engraving'').', 'The sky is clear and blue today.', 'Caelum hodie clarum et caeruleum est.', 'He gazed up at the vast sky.', 'Caelum immensum spectavit.', 'Stars fill the sky at night.', 'Stellae caelum noctu replent.', '{Aer,Aethra}', '{}', 'caelum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (29, 'Virtus', 'Courage, the ability to confront pain or fear.', 'WEER-toos', 'From Latin virtus, derived from vir (''man'').', 'Courage is admired in leaders.', 'Virtus in ducibus admiratur.', 'He showed courage in the face of danger.', 'Coram periculo virtutem ostendit.', 'Courage conquers fear.', 'Virtus metum vincit.', '{Fortitudo,Audacia}', '{Timor}', 'virtus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (30, 'Somnus', 'Sleep, a state of rest for the mind and body.', 'SOM-noos', 'From Latin somnus, derived from Proto-Indo-European *swep-no- (''to sleep'').', 'Sleep restores the body.', 'Somnus corpus restituit.', 'He drifted into a peaceful sleep.', 'In somnum quietum incidit.', 'Sleep is essential for health.', 'Somnus ad valetudinem necessarius est.', '{Quietus,Dormitio}', '{}', 'somnus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (31, 'Poena', 'Punishment, the infliction of a penalty as retribution for an offense.', 'POY-nah', 'From Latin poena, from Greek poinē (''penalty, recompense, punishment'').', 'Justice demands punishment for crime.', 'Iustitia poenam pro crimine postulat.', 'The punishment was severe.', 'Poena gravis erat.', 'He faced punishment for his actions.', 'Propter acta sua poenam sustinuit.', '{Supplicium,Multa}', '{}', 'poena.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (32, 'Vox', 'Voice, the sound produced in a person’s larynx and uttered through the mouth.', 'VOHKS', 'From Proto-Italic *wōks, from Proto-Indo-European *wekʷ- (''to speak'').', 'Her voice was soothing.', 'Vox eius lenis erat.', 'The singer shared his voice with many.', 'Cantor vocem suam multis communicavit.', 'Voices echoed in the hall.', 'Voces in aula resonabant.', '{Clamor,Sonus}', '{}', 'vox.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (33, 'Aetas', 'Age, the length of time a person has lived.', 'AY-tahs', 'From Latin aetas, from aevum (''eternity, age'').', 'With age comes wisdom.', 'Cum aetate sapientia venit.', 'He felt the weight of his age.', 'Aetatis pondus sensit.', 'Age is just a number.', 'Aetas tantum numerus est.', '{Saeculum,Tempus}', '{}', 'aetas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (34, 'Lumen', 'Light, the natural agent that makes things visible.', 'LOO-men', 'From Latin lumen, from Proto-Indo-European *low-men- (''light, brightness'').', 'The lamp provided light for reading.', 'Lumen ad legendum lucem praebuit.', 'Light shone through the window.', 'Lumen per fenestram resplenduit.', 'The light of the moon was soft and gentle.', 'Lumen lunae lene at placidum erat.', '{Lux,Claritas}', '{Tenebrae}', 'lumen.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (35, 'Iuvenis', 'Youth, the period between childhood and adult age.', 'YOO-veh-nis', 'From Proto-Italic *juwenī, from Proto-Indo-European *yuHwen- (''young'').', 'Youth is full of potential.', 'Iuventus plenus potentiae est.', 'She celebrated her youthful spirit.', 'Spiritum iuvenilem celebravit.', 'The youth gathered in the park.', 'Iuvenes in parco convenerunt.', '{Adulescentia,Pueritia}', '{Senectus}', 'iuvenis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (36, 'Felicitas', 'Happiness, the state of being happy.', 'feh-LIHS-i-tahs', 'From Latin felicitas, from felix (''happy, lucky'').', 'Happiness is a warm embrace.', 'Felicitas amplectus calidus est.', 'She found happiness in simple things.', 'Felicitatem in rebus simplicibus invenit.', 'The child''s laughter was pure happiness.', 'Risus pueri felicitas pura erat.', '{Gaudium,Laetitia}', '{Tristitia}', 'felicitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (37, 'Ventus', 'Wind, the perceptible natural movement of the air.', 'VEN-toos', 'From Proto-Italic *wentos, from Proto-Indo-European *h₂wéntos (''wind'').', 'The wind swept across the plains.', 'Ventus per campos spiravit.', 'He felt the cool wind on his face.', 'Ventum frigidum in vultu sensit.', 'Trees bent under the strong wind.', 'Arbores sub vento valido inclinabantur.', '{Aura,Flatus}', '{}', 'ventus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (38, 'Solis', 'Sun, the star at the center of the solar system.', 'SOH-lis', 'From Proto-Italic *sūlis, from Proto-Indo-European *sóh₂wl̥ (''sun'').', 'The sun warms the earth.', 'Sol terram calefacit.', 'He basked in the sun''s glow.', 'In splendore solis sese oblectavit.', 'The sun rises in the east.', 'Sol ab oriente oritur.', '{Phoebus,Titan}', '{}', 'solis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (39, 'Flos', 'Flower, the seed-bearing part of a plant.', 'FLOSS', 'From Proto-Italic *flōs, possibly from a Proto-Indo-European root related to ''bloom''.', 'The garden is full of vibrant flowers.', 'Hortus floribus vivis plenus est.', 'She picked a red flower.', 'Florem rubrum carpsit.', 'Flowers bloomed in the spring.', 'Flores vere floruerunt.', '{Floris,Flosculus}', '{}', 'flos.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (40, 'Nox', 'Night, the period of darkness in each twenty-four hours.', 'NOHKS', 'From Proto-Italic *nokt-s, from Proto-Indo-European *nókʷts (''night'').', 'The stars shine brightly at night.', 'Stellae noctu lucent.', 'He dreams peacefully during the night.', 'Nocte tranquille somniat.', 'Owls hunt during the night.', 'Bubones nocte praedantur.', '{Tenebrae,Vesper}', '{Dies}', 'nox.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (41, 'Fames', 'Hunger, a feeling of discomfort or weakness caused by lack of food.', 'FAH-mes', 'From Proto-Italic *famēs, from Proto-Indo-European *dʰeh₁-m- (''to suck, to drink'').', 'Hunger drives him to seek food.', 'Fames eum ad cibum quaerendum dirigit.', 'The famine left many in hunger.', 'Fames multos fame reliquit.', 'Hunger can overwhelm the mind.', 'Fames animum obruere potest.', '{Inedia,Fames}', '{Satietas}', 'fames.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (42, 'Bellum', 'War, an armed conflict between different nations or states.', 'BEL-lum', 'From Proto-Italic *gʷelom, possibly from Proto-Indo-European *gʷelH- (to strike).', 'War devastates the land.', 'Bellum terram vastat.', 'The soldiers prepare for war.', 'Milites ad bellum se parant.', 'After the war, peace returns.', 'Post bellum, pax redit.', '{Pugna,Proelium}', '{Pax}', 'bellum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (43, 'Sapientia', 'Wisdom, the ability to think and act using knowledge, experience, understanding, and insight.', 'sa-pee-EN-tee-ah', 'Derived from the Latin ''sapiens,'' meaning ''wise'' or ''discerning.''', 'Wisdom is often gained through experience.', 'Sapientia saepe per experientiam acquiritur.', 'She showed great wisdom in her decision.', 'Magnam sapientiam in sua sententia ostendit.', 'Ancient philosophers taught the value of wisdom.', 'Philosophi antiqui sapientiae vim docuerunt.', '{Prudentia,Scientia}', '{Insipientia}', 'sapientia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (44, 'Aurum', 'Gold, a precious metal known for its luster and value.', 'OW-room', 'The word derives from the PIE root ''aus,'' meaning ''to shine.''', 'Gold is considered valuable across many cultures.', 'Aurum apud multas culturas pretiosum habetur.', 'She wore a necklace made of pure gold.', 'Monile ex puro auro factum gerebat.', 'The ancient coins were made of gold.', 'Nummi antiqui ex auro facti sunt.', '{Aurum}', '{Plumbum}', 'aurum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (45, 'Amicus', 'Friend, a person whom one knows and with whom one has a bond of mutual affection.', 'ah-MEE-koos', 'From Latin ''amo,'' meaning ''to love.''', 'A true friend is a treasure.', 'Verus amicus thesaurus est.', 'He trusted his friend''s advice.', 'Amici consilio credidit.', 'They went to the marketplace together.', 'In forum una ierunt.', '{Socius,Comes}', '{Hostis}', 'amicus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (46, 'Libertas', 'Freedom, the power or right to act, speak, or think as one wants.', 'li-BER-tas', 'From the Latin ''liber,'' meaning ''free.''', 'Freedom is a fundamental human right.', 'Libertas ius humanum fundamentale est.', 'The citizens fought for their freedom.', 'Cives pro sua libertate pugnaverunt.', 'Freedom allows for personal and spiritual growth.', 'Libertas ad incrementum personalem et spiritualem permittit.', '{Libertatis,Independencia}', '{Servitus}', 'libertas.jpg');
