INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (56, 'Carus', 'Dear or beloved; cherished by someone.', 'KAH-roos', 'From the Latin ''carus,'' meaning precious or dear.', 'She held her dear memories close to her heart.', 'Memorias caras ad pectus tenuit.', 'His dear friend visited him often.', 'Amicus carus eum saepe visitabat.', 'They exchanged many dear gifts over the years.', 'Multa dona cara post annos commutaverunt.', '{dilectus,amatus}', '{odio}', 'carus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (57, 'Libertas', 'Freedom or liberty; the state of being free.', 'LEE-behr-tas', 'From Latin ''libertas,'' derived from ''liber,'' meaning free.', 'Freedom is a fundamental human right.', 'Libertas ius humanum fundamentale est.', 'The statue represented the city''s dedication to liberty.', 'Statua devotionem urbis ad libertatem repraesentavit.', 'He cherished the freedom to express his thoughts.', 'Libertatem cogitationes exprimendi colebat.', '{autonomia,independentia}', '{servitus}', 'libertas.jpg');
