INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (1, 'Mare', 'Mare means ''sea'' and refers to a large body of salt water that is surrounded in whole or in part by land.', 'MAH-reh', 'From Proto-Indo-European *móri, related to Latin ''Maria'', meaning large body of water.', 'The sailors navigated across the vast sea.', 'Nautae trans magnum mare navigaverunt.', 'The sea was calm and serene at dawn.', 'Mare tranquillum et serenum aurora erat.', 'She gazed at the endless horizon of the sea.', 'Aspiciebat ad infinitum horizontem maris.', '{Oceanus,Pelagus}', '{}', 'mare.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (2, 'Amor', 'Amor means ''love'', describing a profound feeling of affection and attachment.', 'AH-mor', 'From Latin ''amare'', meaning to love or to hold dear.', 'The poet wrote about eternal love.', 'Poeta de aeterno amore scripsit.', 'Her love for music is evident in everything she does.', 'Amor eius ad musicam patet in omnibus quae facit.', 'They showed their love through kindness and understanding.', 'Amorem suum per benignitatem et intellectum ostenderunt.', '{Caritas,Dilectio}', '{Odio}', 'amor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (3, 'Aqua', 'Water, the basic necessity of life.', 'AH-kwah', 'From Proto-Italic *akwā, derived from Proto-Indo-European *h₂ekweh₂.', 'The clear water fills the cup.', 'Aqua clara poculum implet.', 'A river flows with fresh water.', 'Fluvius aqua recenti fluit.', 'They clean the floor with water.', 'Pavimentum aqua purgant.', '{Unda,Liquor}', '{}', 'aqua.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (4, 'Pax', 'Peace, a state of tranquility or quiet.', 'PAHKS', 'From Proto-Italic *paktis, from Proto-Indo-European *peḱ- (''to agree'').', 'The nations seek peace.', 'Gentes pacem quaerunt.', 'Peace reigns in the city.', 'Pax in urbe regnat.', 'His heart is filled with peace.', 'Cor eius pace plenus est.', '{Quies,Tranquillitas}', '{Bellum}', 'pax.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (5, 'Rex', 'King, the male ruler of an independent state.', 'REHKS', 'From Proto-Italic *rēks, from Proto-Indo-European *h₃rḗǵs (''ruler'').', 'The king leads his people.', 'Rex populum suum ducit.', 'A wise king is beloved.', 'Rex sapiens amatur.', 'The king holds court.', 'Rex curiam tenet.', '{Monarch,Regis}', '{Servus}', 'rex.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (178, 'Domus', 'Domus means ''house'' or ''home'', referring to a place where people reside.', 'DOH-moos', 'From Proto-Indo-European *dṓm, meaning ''house''.', 'She returned to her house after a long journey.', 'Post longum iter ad domum suam rediit.', 'The house was large and beautiful.', 'Domus magna et pulchra erat.', 'They gathered at his house for a meeting.', 'Ad domum eius convenerunt ad consilium.', '{Aedes,Tectum}', '{}', 'domus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (7, 'Arbor', 'Tree, a perennial plant with an elongated stem or trunk.', 'AR-bor', 'From Proto-Italic *arɸōs, from Proto-Indo-European *h₂erdʰ- (''to grow'').', 'The tree''s shade is refreshing.', 'Umbra arboris refrigerat.', 'Birds nest in the tree.', 'Aves in arbore nidificant.', 'The tree grows strong and tall.', 'Arbor fortis et alta crescit.', '{Silva,Frutex}', '{}', 'arbor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (8, 'Terra', 'Earth, the land surface of the world.', 'TEHR-ra', 'From Proto-Italic *tersā, from Proto-Indo-European *ters- (''dry'').', 'They cultivate the earth.', 'Terram colunt.', 'The earth is fertile and green.', 'Terra fertilis et viridis est.', 'He marks his territory.', 'Territorium suum notat.', '{Humus,Ager}', '{}', 'terra.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (179, 'Lapis', 'Lapis means ''stone'', referring to a naturally occurring solid aggregate of minerals.', 'LAH-pis', 'Derived from the Proto-Indo-European *leh₁p-, meaning ''stone'' or ''to peel''.', 'He picked up a stone from the ground.', 'Lapidem de terra sustulit.', 'The ancient wall was made of stone.', 'Murus antiquus e lapide factus erat.', 'Stones lined the path through the garden.', 'Lapides semitam per hortum instruxerunt.', '{Saxum,Petra}', '{}', 'lapis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (10, 'Fortuna', 'Fortune, chance or luck regarded as an external arbitrary force.', 'for-TOO-nah', 'From Proto-Italic *fortonā, possibly from Proto-Indo-European *bher- (''to carry'').', 'Fortune favors the brave.', 'Fortuna fortes adiuvat.', 'He takes a risk for fortune.', 'Periculum pro fortuna capiet.', 'Her fortune has changed suddenly.', 'Fortuna eius subito mutata est.', '{Sors,Casus}', '{}', 'fortuna.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (11, 'Insula', 'Island, a tract of land surrounded by water.', 'IN-su-lah', 'From Proto-Italic *enisalā, likely from Proto-Indo-European *eynsula (''island'').', 'The island is lush and green.', 'Insula virens et florida est.', 'They sailed to a distant island.', 'Ad insulam longinquam navigaverunt.', 'The island is surrounded by cliffs.', 'Insula rupibus circumdata est.', '{Aegyptus,Naso}', '{}', 'insula.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (12, 'Stella', 'Star, a luminous point in the night sky.', 'STEL-lah', 'From Proto-Italic *stelhā, from Proto-Indo-European *h₂stḗr (''star'').', 'Stars shine brightly at night.', 'Stellae noctu lucent.', 'The star guides the sailors.', 'Stella nautas ducit.', 'She wishes upon a falling star.', 'Super stellam cadentem votum facit.', '{Astrum,Sidus}', '{}', 'stella.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (13, 'Ignis', 'Fire, the phenomenon of combustion manifested in light, flame, and heat.', 'IG-nis', 'From Proto-Italic *ignis, from Proto-Indo-European *h₁n̥gʷnís (''fire'').', 'Fire warms the hall.', 'Ignis atrium calefacit.', 'The fire blazes brightly.', 'Ignis lucidus ardent.', 'They gathered around the fire.', 'Circa ignem convenerunt.', '{Flamma,Focus}', '{Aqua}', 'ignis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (14, 'Lux', 'Light, the natural agent that stimulates sight and makes things visible.', 'LUKS', 'From Proto-Italic *louks, from Proto-Indo-European *lówkʷs (''light'').', 'Light illuminates the room.', 'Lux conclave illuminat.', 'The dawn brings light.', 'Aurora lucem affert.', 'They follow the light of the lantern.', 'Lucem lanternae sequuntur.', '{Lumen,Clarus}', '{Tenebrae}', 'lux.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (180, 'Campus', 'Campus means ''field'', usually referring to an open space used for agriculture or as a place of activity.', 'KAM-poos', 'Likely from Proto-Indo-European *kamp- meaning ''to bend'' or ''crooked''.', 'The farmer worked in the field from dawn till dusk.', 'Agricola in campo ab aurora ad crepusculum laboravit.', 'The field was covered with wildflowers.', 'Campus floribus agrestibus tectus erat.', 'They played soccer in the open field.', 'In campo aperto pila luserunt.', '{Ager,Arvum}', '{}', 'campus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (16, 'Vitrum', 'Glass, a hard, brittle substance typically transparent or translucent.', 'VIH-trum', 'From Latin vitrum, of uncertain origin but possibly related to vītus (''life'').', 'The glass was filled with water.', 'Vitrum aqua impletum erat.', 'He looked through the clear glass.', 'Per vitrum clarum aspexit.', 'Glass can shatter easily.', 'Vitrum facile frangi potest.', '{Crystallum,Speculum}', '{}', 'vitrum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (17, 'Fluctus', 'Wave, a long body of water curling into an arched form and breaking on the shore.', 'FLUK-tus', 'From Latin, based on fluo (''flow'').', 'Waves crash on the beach.', 'Fluctus in litus impingunt.', 'The waves are strong today.', 'Fluctus hodie fortes sunt.', 'He watched the waves from the shore.', 'A litore fluctus spectabat.', '{Unda,Mari}', '{}', 'fluctus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (18, 'Silva', 'Forest, a large area covered chiefly with trees and undergrowth.', 'SIL-wah', 'From Proto-Italic *silwā, from Proto-Indo-European *selu- (''wood, forest'').', 'The forest is dense and mysterious.', 'Silva densa et mystica est.', 'Animals thrive in the forest.', 'Animalia in silva florent.', 'She wandered through the forest.', 'Per silvam erravit.', '{Nemus,Lucus}', '{}', 'silva.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (181, 'Civitas', 'Civitas means ''state'' or ''citizenship'', often referring to a community or body of citizens.', 'KEE-wee-tahs', 'From Proto-Indo-European *ḱey-, meaning ''to lie down'' or ''settle''.', 'The state provides services to its citizens.', 'Civitas civibus suis officia praebet.', 'He was proud of his Roman citizenship.', 'Civitate sua Romana superbus erat.', 'The laws of the state must be followed.', 'Leges civitatis servandae sunt.', '{Respublica,Urbs}', '{}', 'civitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (19, 'Aurora', 'Dawn, the first light of day.', 'ow-ROH-rah', 'From Latin aurora (''dawn''), associated with the goddess of dawn.', 'The dawn heralds a new day.', 'Aurora novum diem praenunciat.', 'Aurora tinges the sky with colors.', 'Aurora caelum coloribus tingit.', 'He rose with the dawn.', 'Cum aurora surrexit.', '{Diluculum,Ortus}', '{}', 'aurora.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (20, 'Gladius', 'Sword, a weapon with a long blade for cutting or thrusting.', 'GLAH-dee-us', 'From Proto-Italic *gladios, possibly from Proto-Indo-European *gʷlh₂tós (''stab, cut'').', 'The sword glints in the sunlight.', 'Gladius in sole micat.', 'He clenched the sword tightly.', 'Gladium strenue tenuit.', 'The sword was passed down through generations.', 'Gladius generibus traditus est.', '{Spatha,Ensis}', '{}', 'gladius.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (21, 'Uxor', 'Wife, a married woman considered in relation to her spouse.', 'OOK-sore', 'From Proto-Italic *ukᶅisōr, from Proto-Indo-European *ukʷs- (''female'').', 'The wife supported her husband.', 'Uxor maritum adiuvit.', 'She attends the gathering with her wife.', 'Cum uxore convivium frequentat.', 'He spoke fondly of his wife.', 'De uxore sua amanter locutus est.', '{Coniunx,Mulier}', '{}', 'uxor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (22, 'Vita', 'Life, the existence of an individual human being or animal.', 'WIH-tah', 'From Proto-Italic *wītā, from Proto-Indo-European *gʷih₃wós (''alive'').', 'Life is full of adventures.', 'Vita plena est adventuris.', 'She cherishes every moment of her life.', 'Omne momentum vitae suae colit.', 'He leads a simple life.', 'Vitam simplicem ducit.', '{Anima,Existential}', '{Mors}', 'vita.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (23, 'Tempus', 'Time, the indefinite continued progress of existence.', 'TEM-pus', 'From Proto-Italic *tempos, derived from Proto-Indo-European *tem- (''to cut'').', 'Time waits for no one.', 'Tempus neminem manet.', 'They marked the passage of time.', 'Transitum temporis notaverunt.', 'Time moves slowly in peaceful moments.', 'In momentis pacificis tempus lente movetur.', '{Hora,Aetas}', '{}', 'tempus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (182, 'Opus', 'Opus means ''work'', typically referring to a piece of art or a large and important accomplishment.', 'OH-poos', 'From Proto-Indo-European *h₃ep-, meaning ''to work'' or ''perform''.', 'The artist completed his greatest work.', 'Artifex opus suum maximum perfecit.', 'Building the bridge was an arduous work.', 'Pontem aedificare opus arduum erat.', 'Each opus added to his growing reputation.', 'Quodque opus famam eius crescentem auxit.', '{Labor,Operatio}', '{}', 'opus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (25, 'Silentium', 'Silence, the absence of sound.', 'si-len-TEE-um', 'From Latin silentium.', 'The forest was enveloped in silence.', 'Sylva silentio obtecta erat.', 'Silence fell over the room.', 'Silentium super conclave cecidit.', 'He appreciated the silence of the early morning.', 'Silentium primae matutinum aestimavit.', '{Taciturnitas,Quies}', '{Clamor}', 'silentium.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (26, 'Flumen', 'River, a large natural stream of water flowing in a channel to the sea, a lake, or another river.', 'FLOO-men', 'From Latin flūmen, derived from fluō (''flow'').', 'The river flows gently through the valley.', 'Flumen leniter per vallem fluat.', 'He sat by the river and watched the water.', 'Apud flumen sedit et aquam spectavit.', 'Rivers are important for transportation.', 'Flumina sunt ad vecturas magni momenti.', '{Amnis,Rivus}', '{}', 'flumen.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (27, 'Spes', 'Hope, a feeling of expectation and desire for a particular thing to happen.', 'SPAYS', 'From Proto-Italic *speh-, from Proto-Indo-European *spek- (''to look, to observe'').', 'Hope springs eternal in the human breast.', 'Spes aeterna in pectore humano oritur.', 'They have hope for a better future.', 'Spem in melior futura habent.', 'Hope gave them strength to continue.', 'Spes eis vim ad pergendum dedit.', '{Expectatio,Confidentia}', '{Desperatio}', 'spes.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (28, 'Caelum', 'Sky, the expanse of air over any given point on the earth.', 'KAI-lum', 'From Proto-Italic *kaiwelo-, from Proto-Indo-European *ḱaywelo- (''the act of carving, engraving'').', 'The sky is clear and blue today.', 'Caelum hodie clarum et caeruleum est.', 'He gazed up at the vast sky.', 'Caelum immensum spectavit.', 'Stars fill the sky at night.', 'Stellae caelum noctu replent.', '{Aer,Aethra}', '{}', 'caelum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (29, 'Virtus', 'Courage, the ability to confront pain or fear.', 'WEER-toos', 'From Latin virtus, derived from vir (''man'').', 'Courage is admired in leaders.', 'Virtus in ducibus admiratur.', 'He showed courage in the face of danger.', 'Coram periculo virtutem ostendit.', 'Courage conquers fear.', 'Virtus metum vincit.', '{Fortitudo,Audacia}', '{Timor}', 'virtus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (30, 'Somnus', 'Sleep, a state of rest for the mind and body.', 'SOM-noos', 'From Latin somnus, derived from Proto-Indo-European *swep-no- (''to sleep'').', 'Sleep restores the body.', 'Somnus corpus restituit.', 'He drifted into a peaceful sleep.', 'In somnum quietum incidit.', 'Sleep is essential for health.', 'Somnus ad valetudinem necessarius est.', '{Quietus,Dormitio}', '{}', 'somnus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (31, 'Poena', 'Punishment, the infliction of a penalty as retribution for an offense.', 'POY-nah', 'From Latin poena, from Greek poinē (''penalty, recompense, punishment'').', 'Justice demands punishment for crime.', 'Iustitia poenam pro crimine postulat.', 'The punishment was severe.', 'Poena gravis erat.', 'He faced punishment for his actions.', 'Propter acta sua poenam sustinuit.', '{Supplicium,Multa}', '{}', 'poena.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (32, 'Vox', 'Voice, the sound produced in a person’s larynx and uttered through the mouth.', 'VOHKS', 'From Proto-Italic *wōks, from Proto-Indo-European *wekʷ- (''to speak'').', 'Her voice was soothing.', 'Vox eius lenis erat.', 'The singer shared his voice with many.', 'Cantor vocem suam multis communicavit.', 'Voices echoed in the hall.', 'Voces in aula resonabant.', '{Clamor,Sonus}', '{}', 'vox.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (33, 'Aetas', 'Age, the length of time a person has lived.', 'AY-tahs', 'From Latin aetas, from aevum (''eternity, age'').', 'With age comes wisdom.', 'Cum aetate sapientia venit.', 'He felt the weight of his age.', 'Aetatis pondus sensit.', 'Age is just a number.', 'Aetas tantum numerus est.', '{Saeculum,Tempus}', '{}', 'aetas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (34, 'Lumen', 'Light, the natural agent that makes things visible.', 'LOO-men', 'From Latin lumen, from Proto-Indo-European *low-men- (''light, brightness'').', 'The lamp provided light for reading.', 'Lumen ad legendum lucem praebuit.', 'Light shone through the window.', 'Lumen per fenestram resplenduit.', 'The light of the moon was soft and gentle.', 'Lumen lunae lene at placidum erat.', '{Lux,Claritas}', '{Tenebrae}', 'lumen.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (35, 'Iuvenis', 'Youth, the period between childhood and adult age.', 'YOO-veh-nis', 'From Proto-Italic *juwenī, from Proto-Indo-European *yuHwen- (''young'').', 'Youth is full of potential.', 'Iuventus plenus potentiae est.', 'She celebrated her youthful spirit.', 'Spiritum iuvenilem celebravit.', 'The youth gathered in the park.', 'Iuvenes in parco convenerunt.', '{Adulescentia,Pueritia}', '{Senectus}', 'iuvenis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (36, 'Felicitas', 'Happiness, the state of being happy.', 'feh-LIHS-i-tahs', 'From Latin felicitas, from felix (''happy, lucky'').', 'Happiness is a warm embrace.', 'Felicitas amplectus calidus est.', 'She found happiness in simple things.', 'Felicitatem in rebus simplicibus invenit.', 'The child''s laughter was pure happiness.', 'Risus pueri felicitas pura erat.', '{Gaudium,Laetitia}', '{Tristitia}', 'felicitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (37, 'Ventus', 'Wind, the perceptible natural movement of the air.', 'VEN-toos', 'From Proto-Italic *wentos, from Proto-Indo-European *h₂wéntos (''wind'').', 'The wind swept across the plains.', 'Ventus per campos spiravit.', 'He felt the cool wind on his face.', 'Ventum frigidum in vultu sensit.', 'Trees bent under the strong wind.', 'Arbores sub vento valido inclinabantur.', '{Aura,Flatus}', '{}', 'ventus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (38, 'Solis', 'Sun, the star at the center of the solar system.', 'SOH-lis', 'From Proto-Italic *sūlis, from Proto-Indo-European *sóh₂wl̥ (''sun'').', 'The sun warms the earth.', 'Sol terram calefacit.', 'He basked in the sun''s glow.', 'In splendore solis sese oblectavit.', 'The sun rises in the east.', 'Sol ab oriente oritur.', '{Phoebus,Titan}', '{}', 'solis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (39, 'Flos', 'Flower, the seed-bearing part of a plant.', 'FLOSS', 'From Proto-Italic *flōs, possibly from a Proto-Indo-European root related to ''bloom''.', 'The garden is full of vibrant flowers.', 'Hortus floribus vivis plenus est.', 'She picked a red flower.', 'Florem rubrum carpsit.', 'Flowers bloomed in the spring.', 'Flores vere floruerunt.', '{Floris,Flosculus}', '{}', 'flos.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (40, 'Nox', 'Night, the period of darkness in each twenty-four hours.', 'NOHKS', 'From Proto-Italic *nokt-s, from Proto-Indo-European *nókʷts (''night'').', 'The stars shine brightly at night.', 'Stellae noctu lucent.', 'He dreams peacefully during the night.', 'Nocte tranquille somniat.', 'Owls hunt during the night.', 'Bubones nocte praedantur.', '{Tenebrae,Vesper}', '{Dies}', 'nox.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (41, 'Fames', 'Hunger, a feeling of discomfort or weakness caused by lack of food.', 'FAH-mes', 'From Proto-Italic *famēs, from Proto-Indo-European *dʰeh₁-m- (''to suck, to drink'').', 'Hunger drives him to seek food.', 'Fames eum ad cibum quaerendum dirigit.', 'The famine left many in hunger.', 'Fames multos fame reliquit.', 'Hunger can overwhelm the mind.', 'Fames animum obruere potest.', '{Inedia,Fames}', '{Satietas}', 'fames.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (42, 'Bellum', 'War, an armed conflict between different nations or states.', 'BEL-lum', 'From Proto-Italic *gʷelom, possibly from Proto-Indo-European *gʷelH- (to strike).', 'War devastates the land.', 'Bellum terram vastat.', 'The soldiers prepare for war.', 'Milites ad bellum se parant.', 'After the war, peace returns.', 'Post bellum, pax redit.', '{Pugna,Proelium}', '{Pax}', 'bellum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (43, 'Sapientia', 'Wisdom, the ability to think and act using knowledge, experience, understanding, and insight.', 'sa-pee-EN-tee-ah', 'Derived from the Latin ''sapiens,'' meaning ''wise'' or ''discerning.''', 'Wisdom is often gained through experience.', 'Sapientia saepe per experientiam acquiritur.', 'She showed great wisdom in her decision.', 'Magnam sapientiam in sua sententia ostendit.', 'Ancient philosophers taught the value of wisdom.', 'Philosophi antiqui sapientiae vim docuerunt.', '{Prudentia,Scientia}', '{Insipientia}', 'sapientia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (44, 'Aurum', 'Gold, a precious metal known for its luster and value.', 'OW-room', 'The word derives from the PIE root ''aus,'' meaning ''to shine.''', 'Gold is considered valuable across many cultures.', 'Aurum apud multas culturas pretiosum habetur.', 'She wore a necklace made of pure gold.', 'Monile ex puro auro factum gerebat.', 'The ancient coins were made of gold.', 'Nummi antiqui ex auro facti sunt.', '{Aurum}', '{Plumbum}', 'aurum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (45, 'Amicus', 'Friend, a person whom one knows and with whom one has a bond of mutual affection.', 'ah-MEE-koos', 'From Latin ''amo,'' meaning ''to love.''', 'A true friend is a treasure.', 'Verus amicus thesaurus est.', 'He trusted his friend''s advice.', 'Amici consilio credidit.', 'They went to the marketplace together.', 'In forum una ierunt.', '{Socius,Comes}', '{Hostis}', 'amicus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (46, 'Libertas', 'Freedom, the power or right to act, speak, or think as one wants.', 'li-BER-tas', 'From the Latin ''liber,'' meaning ''free.''', 'Freedom is a fundamental human right.', 'Libertas ius humanum fundamentale est.', 'The citizens fought for their freedom.', 'Cives pro sua libertate pugnaverunt.', 'Freedom allows for personal and spiritual growth.', 'Libertas ad incrementum personalem et spiritualem permittit.', '{Libertatis,Independencia}', '{Servitus}', 'libertas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (47, 'Animus', 'Mind or soul, the spiritual or mental essence of a person.', 'AH-nee-moos', 'From Latin ''anima,'' meaning ''soul'' or ''spirit.''', 'The mind can be both a fortress and a prison.', 'Animus potest et arx et carcer esse.', 'She found strength in her mind''s resilience.', 'Fortitudine in animi constantia invenit.', 'The health of the soul influences the body.', 'Sanitas animi corpus afficit.', '{Mens,Spiritus}', '{}', 'animus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (48, 'Vitium', 'Vice or fault, a moral failing or error.', 'WI-tee-um', 'Derived from the Latin term ‘vitare,’ meaning ‘to avoid.’', 'Greed can be considered a vice.', 'Avaritia vitium haberi potest.', 'Acknowledging one''s own faults is crucial for growth.', 'Suos vitia agnoscere ad incrementum necessarium est.', 'They sought to correct their faults.', 'Vitia sua corrigere temptaverunt.', '{Culpa,Error}', '{Virtus}', 'vitium.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (49, 'Ignavia', 'Laziness or cowardice, a reluctance to act or lack of courage.', 'ig-NAH-wee-ah', 'From Latin ''ignavus,'' meaning ''idle'' or ''cowardly.''', 'Laziness can prevent success.', 'Ignavia successum impedire potest.', 'He faced his fears instead of succumbing to cowardice.', 'Metus suos potius quam ignaviae succubuit.', 'Their laziness led to missed opportunities.', 'Ignavia occasiones amissae duxit.', '{Acedia}', '{Industria,Fortitudo}', 'ignavia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (50, 'Caelum', 'Sky or heaven, the expanse of air over the Earth.', 'KAI-loom', 'Derived from the Latin term ‘coelum’ of unknown origin.', 'The sky was a brilliant blue.', 'Caelum caeruleum splendet.', 'Birds flew across the morning sky.', 'Aves trans caelum matutinum volaverunt.', 'He gazed at the stars scattered across the heavens.', 'Aspexit astra per caelum sparsa.', '{Aether}', '{}', 'caelum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (51, 'Vinum', 'Wine, an alcoholic drink made from fermented grapes.', 'VEE-noom', 'From the Latin ''vinum,'' possibly from a Proto-Italic or possibly Latin origination.', 'Wine has been enjoyed for centuries.', 'Vinum per saecula delectatum est.', 'They celebrated with a bottle of fine wine.', 'Cum poculo vini pretiosi celebraverunt.', 'The wine flowed freely at the banquet.', 'Vinum libere in convivio fluxit.', '{Caecubum,Falernum}', '{}', 'vinum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (52, 'Murus', 'Wall, a vertical structure used for enclosing an area.', 'MU-roos', 'Directly derived from Latin, potentially associated with the Greek ‘μαστός’ (mastós).', 'The wall protected the city from invaders.', 'Murus urbem ab hostibus defendit.', 'They climbed over the garden wall.', 'Trans murum hortum ascenderunt.', 'A mural was painted on the wall.', 'Murus pictura ornatus est.', '{Paries}', '{}', 'murus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (53, 'Amicus', 'A friend or ally; someone with whom one shares mutual affection.', 'ah-MEE-kus', 'Derived from the Latin ''amicus,'' meaning friend.', 'The companion stayed loyal during all their trials.', 'Amicus per omnes difficultates fidelis mansit.', 'Marcus greeted his friend with open arms.', 'Marcus amicum bracchiis apertis salutavit.', 'A true friend is a rare treasure.', 'Amicus verus rara gemma est.', '{socius,consors}', '{inimicus}', 'amicus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (54, 'Bellus', 'Pretty or charming, often used to describe people or things that are pleasing to the senses.', 'BEH-loos', 'From the diminutive of ''bonus,'' meaning good.', 'The flowers in the garden were exquisitely beautiful.', 'Flores in horto erant egregie belli.', 'She wore a lovely dress to the banquet.', 'Vestem bellam ad convivium gerebat.', 'His smile was both warm and charming.', 'Risus eius et calidus et bellus erat.', '{pulcher,venustus}', '{}', 'bellus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (55, 'Aquila', 'An eagle; a large bird of prey known for its strength and keen sight.', 'AH-kwee-lah', 'Derived from the Latin word for eagle.', 'The eagle soared high above the hills.', 'Aquila super colles alte volavit.', 'In ancient Rome, the eagle symbolized power and authority.', 'In Roma antiqua, aquila potestatem auctoritatemque significabat.', 'With a sharp eye, the eagle hunted for its prey.', 'Acuto oculo, aquila praedam suam venabatur.', '{volucris,raptor}', '{}', 'aquila.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (56, 'Carus', 'Dear or beloved; cherished by someone.', 'KAH-roos', 'From the Latin ''carus,'' meaning precious or dear.', 'She held her dear memories close to her heart.', 'Memorias caras ad pectus tenuit.', 'His dear friend visited him often.', 'Amicus carus eum saepe visitabat.', 'They exchanged many dear gifts over the years.', 'Multa dona cara post annos commutaverunt.', '{dilectus,amatus}', '{odio}', 'carus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (57, 'Libertas', 'Freedom or liberty; the state of being free.', 'LEE-behr-tas', 'From Latin ''libertas,'' derived from ''liber,'' meaning free.', 'Freedom is a fundamental human right.', 'Libertas ius humanum fundamentale est.', 'The statue represented the city''s dedication to liberty.', 'Statua devotionem urbis ad libertatem repraesentavit.', 'He cherished the freedom to express his thoughts.', 'Libertatem cogitationes exprimendi colebat.', '{autonomia,independentia}', '{servitus}', 'libertas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (58, 'Gloria', 'Glory or renown; great honor and admiration gained by achievements.', 'GLOH-ree-ah', 'From Latin ''gloria,'' meaning fame or admiration.', 'The warrior sought glory on the battlefield.', 'Gloriam in acie miles quaesivit.', 'Glory was depicted in his every deed.', 'Gloria in omnibus factis eius depicta est.', 'They celebrated the glory of their victorious leader.', 'Gloriam ducis victoriosi celebraverunt.', '{fama,laus}', '{}', 'gloria.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (59, 'Fortis', 'Brave or strong; possessing courage.', 'FOR-tis', 'Derived from Latin ''fortis,'' meaning strong or powerful.', 'The strong knight faced the dragon without fear.', 'Miles fortis unam sine metu oppugnavit draconem.', 'His bravery in battles was renowned.', 'Fortitudo eius in proeliis clara erat.', 'Strength can be measured in many ways.', 'Fortia in multis modis metiri potest.', '{audax,validus}', '{ignavus}', 'fortis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (60, 'Pax', 'Peace; a state of tranquility or freedom from war.', 'PAHKS', 'From Latin ''pax,'' meaning peace.', 'Peace reigned throughout the land.', 'Pax per terram regnavit.', 'She longed for peace and quiet after the battle.', 'Pacem et silentium post proelium desideravit.', 'The treaty brought peace to the region.', 'Foedus pacem regioni attulit.', '{tranquillitas,serenitas}', '{bellum}', 'pax.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (61, 'Caelum', 'Sky or heaven; the expanse of air over the earth.', 'KAI-loom', 'From Proto-Italic ''kailom,'' referring to the heavens or the firmament.', 'The sky was clear and blue.', 'Caelum serenum et caeruleum erat.', 'Stars twinkled in the night sky.', 'Stellae in caelo nocturno micabant.', 'He gazed up at the sky in wonder.', 'Caelum adspiciens admiratus est.', '{aether,firmamentum}', '{}', 'caelum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (62, 'Virtus', 'Courage or virtue; the qualities of being morally good or brave.', 'WEER-toos', 'From Latin ''vir,'' meaning man, implying manliness or valor.', 'Virtues guide us to lead honorable lives.', 'Virtutes ad vitam honestam ducunt.', 'He was celebrated for his virtue and wisdom.', 'Propter virtutem et sapientiam celebratus est.', 'The hero''s courage was unmatched.', 'Virtus herois incomparabilis erat.', '{fortitudo,integritas}', '{vitium}', 'virtus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (63, 'Mors', 'Death; the end of life or a cessation of being.', 'mōrs', 'From Proto-Indo-European ''mr̥tós,'' meaning dead or death.', 'Death comes to all living creatures.', 'Mors omnibus creaturis vivis venit.', 'He contemplated life and death with a heavy heart.', 'De vita et morte pectore ingravescente meditatus est.', 'In the stories, death was often depicted as a dark figure.', 'In fabulis, mors saepe ut imago obscura depicta est.', '{exitus,obitus}', '{vita}', 'mors.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (64, 'Luxuria', 'Luxury; great comfort or extravagant living.', 'look-SOO-ree-ah', 'Derived from Latin ''luxus,'' meaning excess or extravagance.', 'They lived a life of luxury and splendor.', 'Vitam luxuriae et splendoris egerunt.', 'The banquet was a feast of luxury.', 'Conviviium luxuriosa epula erat.', 'Luxury was evident in every corner of the estate.', 'Luxuria in omni angulo fundi apparebat.', '{opulentia,splendor}', '{simplicitas}', 'luxuria.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (65, 'Sapiens', 'Wise or discerning; possessing or showing intelligence and good judgment.', 'SAH-pee-ens', 'From Latin ''sapere,'' meaning to taste, to know.', 'He was known for his wise counsel.', 'Consiliis sapientibus notus erat.', 'In difficult times, the wise remain calm.', 'In temporibus difficilibus, sapientes tranquilli manent.', 'Her wisdom guided her through life''s challenges.', 'Sapientia eius per vitae difficultates duxit.', '{prudens,sagax}', '{stultus}', 'sapiens.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (66, 'Natura', 'Nature; the inherent qualities or phenomena of the physical world.', 'nah-TOO-rah', 'From Latin ''natus,'' meaning born, referring to birth or origin.', 'She admired the wonders of nature.', 'Miracula naturae admirabatur.', 'Nature offers solace to those who seek it.', 'Natura solatium praebet petentibus.', 'The beauty of nature is unparalleled.', 'Pulchritudo naturae incomparabilis est.', '{universum,mundus}', '{}', 'natura.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (67, 'Solus', 'Alone or solitary; being by oneself.', 'SOH-loos', 'From Proto-Indo-European ''swo,'' meaning one’s own.', 'He wandered the empty halls alone.', 'Per atria vacua solus ambulavit.', 'She enjoyed her solitary walks in the garden.', 'Ambulationes solitarias in horto amavit.', 'Being alone, he found peace.', 'Solus, pacem invenit.', '{singularis,unicus}', '{socius}', 'solus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (68, 'Vitium', 'Vice or defect; a moral failing or flaw.', 'VEE-tee-oom', 'From Latin ''vitium,'' meaning fault or defect.', 'His vices led to his downfall.', 'Vitium eius ad casum duxit.', 'The book described the vices of men in power.', 'Liber vitia virorum in potestate descripsit.', 'Overcoming vice is a noble endeavor.', 'Vitium superare arduum opus est.', '{defectus,culpa}', '{virtus}', 'vitium.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (69, 'Celeritas', 'Speed or swiftness; the quality of being fast.', 'keh-LEH-ree-tas', 'Derived from the Latin ''celeris,'' meaning swift.', 'The athlete''s speed on the track was impressive.', 'Celeritas athletae in cursu memorabilis erat.', 'He moved with the swiftness of a cheetah.', 'Cum velocitate cephei se movebat.', 'Speed is crucial in a race.', 'Celeritas in cursu crucialis est.', '{velocitas,rapidity}', '{tarditas}', 'celeritas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (70, 'Fides', 'Faith or trust; confidence in someone or something.', 'FEE-days', 'From Latin ''fidere,'' meaning to trust.', 'Trust in others builds strong relationships.', 'Fides aliis relationes fortes confirmat.', 'He placed his faith in his comrades.', 'Fidem in sodales suos posuere.', 'Their faith never wavered, even in hard times.', 'Fides eorum numquam vacillavit, etiam temporibus duris.', '{confidentia,reliance}', '{credulus}', 'fides.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (71, 'Silentium', 'Silence or quietude; the absence of sound.', 'see-LEN-tee-um', 'From Latin ''silere,'' meaning to be silent.', 'The library was filled with silence as everyone studied.', 'Bibliotheca silentio repleta erat dum omnes studebant.', 'In the early morning hours, silence reigned.', 'In primis horis matutinis silentium regnavit.', 'A moment of silence was observed for reflection.', 'Momentum silentii ad cogitationem observatum est.', '{quies,tranquillitas}', '{clamor}', 'silentium.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (72, 'Labor', 'Work or toil; physical or mental exertion.', 'LAH-bor', 'From Latin ''labor,'' denoting toil or physical work.', 'Their hard work paid off with the harvest.', 'Labor durus cum messu frutus est.', 'Labor brings wealth and knowledge.', 'Labor opes et scientiam affert.', 'She labored tirelessly in the fields.', 'In agris indefatigabiliter laboravit.', '{opus,industria}', '{otium}', 'labor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (183, 'Carmen', 'Carmen means ''song'' or ''poem'', referring to a piece of music or a work of poetry.', 'KAR-men', 'Derived from Proto-Indo-European *kan-, meaning ''to sing''.', 'She wrote a beautiful song for the festival.', 'Carmen pulchrum ad festum scripsit.', 'The poem moved everyone to tears.', 'Carmen omnes ad lacrimas movit.', 'His song was heard throughout the land.', 'Carmen eius per totam terram auditum est.', '{Cantus,Poema}', '{}', 'carmen.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (74, 'Libertas', 'Freedom or liberty, a state of being free rather than in confinement or under physical restraint.', 'LIH-ber-tas', 'Derived from Latin ''liber'', meaning free.', 'Freedom is cherished by all.', 'Libertas a cunctis colitur.', 'He fought for liberty.', 'Pro libertate pugnavit.', 'Liberty is the foundation of democracy.', 'Libertas fundamentum democratiae est.', '{Emancipatio,Libertatis}', '{Servitus,Captivitas}', 'libertas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (75, 'Lumen', 'Light, a natural agent that stimulates sight and makes things visible.', 'LOO-men', 'From Latin ''lumen'', meaning light or brightness.', 'The candle provided a warm light.', 'Candela lumen calidum praebuit.', 'Light brightens the room.', 'Lumen conclave illuminat.', 'She works better in good light.', 'Melius in bono lumine laborat.', '{Lux,Clarus}', '{Tenebrae,Umbra}', 'lumen.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (184, 'Caput', 'Caput means ''head'', often used to refer to a person''s head or the topmost part of something.', 'KA-put', 'From Proto-Italic *kaput, going back to Proto-Indo-European *káput, meaning ''head''.', 'He nodded his head in agreement.', 'Caput adnuit consentiens.', 'The statue''s head was carefully sculpted.', 'Caput statuae diligenter sculptum erat.', 'She wore a hat upon her head.', 'Pileum in capite gerebat.', '{Vertex,Summum}', '{}', 'caput.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (77, 'Mare', 'Sea, a vast body of salt water that covers much of the earth''s surface.', 'MAH-ray', 'Tracing back to Proto-Indo-European ''*móri'', related to water.', 'The sea is calm today.', 'Mare hodie tranquillum est.', 'They sailed across the sea.', 'Trans mare navigaverunt.', 'The sun sets over the sea.', 'Sol supra mare occidit.', '{Oceanus}', '{Terra}', 'mare.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (185, 'Cor', 'Cor means ''heart'', symbolically often used to refer to emotion, affection, or passion.', 'KOR', 'From Proto-Italic *kord, originating from Proto-Indo-European *ḱḗr, meaning ''heart''.', 'His heart was filled with joy.', 'Cor eius gaudio plenum erat.', 'She gave her heart to him.', 'Cor suum ei dedit.', 'Their hearts were united in love.', 'Corda eorum amore coniuncta erant.', '{Pectus,Anima}', '{}', 'cor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (186, 'Portus', 'Portus means ''port'' or ''harbor'', indicating a place where ships dock.', 'POR-toos', 'From Proto-Indo-European *prtu-, meaning ''passage'' or ''crossing''.', 'The ship arrived at the port.', 'Navis ad portum pervenit.', 'The harbor was bustling with activity.', 'Portus negotiis fervebat.', 'They sailed from the port at sunrise.', 'E portu sole oriente navigaverunt.', '{Navale,Haven}', '{}', 'portus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (187, 'Soror', 'Soror means ''sister'', indicating a female sibling.', 'SOH-ror', 'From Proto-Indo-European *swésōr, meaning ''sister''.', 'Her sister helped her with her homework.', 'Soror eam in studiis adiuvit.', 'The sisters shared everything.', 'Sorores omnia inter se communicaverunt.', 'She has a younger sister.', 'Sororem minorem natu habet.', '{Germana,Sodalis}', '{}', 'soror.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (188, 'Corvus', 'Corvus means ''crow'' or ''raven'', referring to a type of large black bird.', 'KOR-woos', 'From Proto-Indo-European *ḱor-, an onomatopoeic root.', 'The crow cawed loudly from the tree.', 'Corvus de arbore clare crocitavit.', 'Ravens are known for their intelligence.', 'Corvi propter ingenium notantur.', 'The flock of crows flew over the field.', 'Grex corvorum super campum volavit.', '{Avis,Niger}', '{}', 'corvus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (189, 'Rosa', 'Rosa means ''rose'', a type of flowering shrub known for its beauty and fragrance.', 'ROH-sa', 'From Ancient Greek ῥόδον (rhódon), itself from an Iranian root.', 'The rose bloomed in the garden.', 'Rosa in horto floruit.', 'She received a bouquet of roses.', 'Fasciculum rosarum accepit.', 'The scent of roses filled the air.', 'Odor rosarum aerem implevit.', '{Flos,Ver}', '{}', 'rosa.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (190, 'Liber', 'Liber means ''book'', referring to a set of written, printed, or blank sheets bound together.', 'LEE-ber', 'Possibly from Proto-Indo-European *leyǵ-, meaning ''to collect''.', 'He borrowed a book from the library.', 'Librum e bibliotheca mutuatus est.', 'The book was full of fascinating stories.', 'Liber fabulis miris plenus erat.', 'She spent the afternoon reading a book.', 'Post meridiem librum legens consumpsit.', '{Codex,Volumen}', '{}', 'liber.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (191, 'Luna', 'Luna means ''moon'', the natural satellite of the Earth.', 'LOO-na', 'From Proto-Italic *louksnā, from Proto-Indo-European *lewk-.', 'The moon shone brightly in the night sky.', 'Luna in caelo nocturno clare lucebat.', 'They marveled at the full moon.', 'Lunam plenam mirati sunt.', 'He gazed at the moon through his telescope.', 'Lunam per perspicillum spectavit.', '{Selene,Noctiluca}', '{}', 'luna.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (192, 'Avis', 'Avis means ''bird'', referring to a warm-blooded, egg-laying vertebrate animal.', 'AH-wees', 'From Proto-Indo-European *h₂aw-, meaning ''bird''.', 'The bird sang a cheerful song.', 'Avis carmen laetum cecinit.', 'Birds flew south for the winter.', 'Aves hieme ad meridiem volaverunt.', 'He watched the birds through his window.', 'Aves per fenestram spectavit.', '{Volucris,Aliger}', '{}', 'avis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (193, 'Mons', 'Mons means ''mountain'', a large natural elevation rising from the Earth''s surface.', 'MONS', 'From Proto-Indo-European *men- meaning ''to project''.', 'The mountain was covered in snow.', 'Mons nive tectus erat.', 'They hiked to the top of the mountain.', 'Ad summum montem ascenderunt.', 'The view from the mountain was breathtaking.', 'Prospectus de monte mirabilis erat.', '{Collis,Clivus}', '{}', 'mons.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (194, 'Domina', 'Domina means ''lady'' or ''mistress'', indicating a woman of authority or status.', 'DOH-mee-na', 'From the Latin dominus, meaning ''lord'' or ''master''.', 'The lady of the house greeted her guests.', 'Domina domus hospites suos salutavit.', 'She was known as a kind and wise mistress.', 'Domina benigna et sapiens habebatur.', 'Her role as a lady was respected by all.', 'Munus eius dominae ab omnibus honorabatur.', '{Matrona,Femina}', '{}', 'domina.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (195, 'Pes', 'Pes means ''foot'', the lower extremity of the leg used for walking or standing.', 'PES', 'From Proto-Italic *pēs, derived from Proto-Indo-European *ped-.', 'He injured his foot while playing soccer.', 'Pedem laesit dum pila ludit.', 'She dipped her feet into the cool water.', 'Pedes in aquam frigidam demisit.', 'The athlete''s feet moved swiftly across the track.', 'Pedes athletae celeriter per stadium cucurrerunt.', '{Gressus,Planta}', '{}', 'pes.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (196, 'Sagitta', 'Sagitta means ''arrow'', a projectile shot with a bow.', 'sa-GEET-ta', 'From Proto-Indo-European *sekw-, meaning ''to follow'' or ''pursue''.', 'The archer''s arrow hit the target.', 'Sagitta sagittarii scopum percussit.', 'She collected arrows after the competition.', 'Sagittas post certamen collegit.', 'Arrows flew through the air in the battle.', 'Sagittae per aerem in proelio volaverunt.', '{Telum,Jaculum}', '{}', 'sagitta.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (197, 'Severitas', 'Severitas means ''severity'' or ''seriousness'', referring to strictness, sternness, or harshness.', 'se-WEH-ree-tas', 'From the Latin severus, meaning ''stern'' or ''austere''.', 'The teacher was known for her severity.', 'Magistra severitate sua nota erat.', 'He appreciated the seriousness of the situation.', 'Severitatem rei intellexit.', 'The law was enforced with severity.', 'Lex cum severitate exercebatur.', '{Gravitas,Austeritas}', '{Lenitas}', 'severitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (198, 'Aestas', 'Aestas means summer, the warmest season of the year typically associated with growth and abundance.', 'AY-estas', 'Derived from Proto-Indo-European *h₂eyd- meaning to burn or heat.', 'In summer, children play outside all day.', 'Aestate liberi totum diem foris ludunt.', 'The crops grow well in the summer heat.', 'Fruges aestate calida bene crescunt.', 'Our family goes on vacation every summer.', 'Familia nostra omni aestate peregrinatur.', '{Veranum,Aestas}', '{Hiems}', 'aestas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (199, 'Felis', 'Felis means cat, a small domesticated carnivorous mammal with soft fur, a short snout, and retractable claws.', 'FAY-lis', 'Borrowed from Ancient Greek φῆλις (phêlis), likely from a non-Indo-European source.', 'The cat chased the mouse across the yard.', 'Felis murem per aream persecuta est.', 'She adopted a kitten from the shelter.', 'Catulum felis adoptavit.', 'Cats are known for their independence.', 'Feles libertate sua notae sunt.', '{Catulus,Murus}', '{}', 'felis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (200, 'Pecunia', 'Pecunia means money, a medium of exchange in the form of coins and banknotes.', 'peh-KOO-nee-ah', 'From pecus meaning cattle, originally indicating wealth in livestock.', 'He saved his money for a rainy day.', 'Pecuniam suam in diem adversum servavit.', 'She invested her money wisely.', 'Pecuniam suam sapienter collocavit.', 'The artist gained fame and money.', 'Artifex famam et pecuniam adeptus est.', '{Nummus,Sestertius}', '{}', 'pecunia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (201, 'Fons', 'Fons means spring or fountain, a natural source of water.', 'FONS', 'From Proto-Indo-European *dʰegʷʰ- meaning to burn or to form bubbles.', 'They discovered a spring in the forest.', 'Fontem in silva invenerunt.', 'The fountain in the courtyard was beautiful.', 'Fons in area aedium pulcher erat.', 'The town was built near a natural spring.', 'Oppidum prope fontem naturalem conditum est.', '{Scaturigo,Iugulum}', '{}', 'fons.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (202, 'Felis', 'Felis means cat, a small domesticated carnivorous mammal with soft fur, a short snout, and retractable claws.', 'FAY-lis', 'Borrowed from Ancient Greek φῆλις (phêlis), likely from a non-Indo-European source.', 'The cat chased the mouse across the yard.', 'Felis murem per aream persecuta est.', 'She adopted a kitten from the shelter.', 'Catulum felis adoptavit.', 'Cats are known for their independence.', 'Feles libertate sua notae sunt.', '{Catulus,Murus}', '{}', 'felis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (203, 'Mens', 'Mens means mind, the element of a person that enables them to think and feel.', 'MENS', 'From Proto-Indo-European *men- meaning to think.', 'A calm mind leads to wise decisions.', 'Mens tranquilla ad sapientes sententias ducit.', 'He trained his mind for focus.', 'Mentem suam ad attentionem exercuit.', 'The mind is a powerful tool.', 'Mens instrumentum potens est.', '{Animus,Intellectus}', '{}', 'mens.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (204, 'Ars', 'Ars means art, skill acquired by experience, study, or observation.', 'ARS', 'From Proto-Indo-European *h₂r̥tís meaning arrangement or order.', 'Art reflects the culture of a society.', 'Ars culturam societatis exprimit.', 'She studied the art of painting.', 'Artem pingendi didicit.', 'The building was a work of art.', 'Aedificium opus artis erat.', '{Ingenium,Doctrina}', '{}', 'ars.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (205, 'Humanitas', 'Humanitas means humanity, the quality of being humane and benevolent.', 'hoo-mah-NEE-tas', 'From Latin humanus meaning human.', 'Humanity should strive to protect the environment.', 'Humanitas naturam tueri debet.', 'Acts of kindness show our humanity.', 'Beneficia humanitatem nostram ostendunt.', 'She dedicated her life to serving humanity.', 'Vitam suam humanitati serviendae dedicavit.', '{Clementia,Benevolentia}', '{}', 'humanitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (206, 'Pactum', 'Pactum means agreement or pact, a formal arrangement between two or more parties.', 'PAHK-toom', 'From pacisci meaning to agree or make a bargain.', 'The countries signed a peace agreement.', 'Gentes pactum pacis signaverunt.', 'They reached an agreement after long discussions.', 'Post longas disputationes ad pactum pervenerunt.', 'A verbal agreement was not enough.', 'Pactum verbis tantum factum satis non erat.', '{Foedus,Conventio}', '{}', 'pactum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (207, 'Dentis', 'Dentis means tooth, a hard, calcified structure in the jaws used for eating.', 'DEN-tees', 'From Proto-Indo-European *h₃dónts meaning tooth.', 'Brush your teeth twice a day.', 'Dentes bis in die purga.', 'He lost a tooth during the game.', 'Dentem in ludo amisit.', 'She had a cavity in one tooth.', 'Cavum in dente habebat.', '{Mola,Incisor}', '{}', 'dentis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (208, 'Aequor', 'Aequor means sea or ocean, the large body of salt water that covers most of Earth''s surface.', 'AY-kwor', 'From aequus meaning even or level, referring to the flat surface of the sea.', 'The ocean is vast and mysterious.', 'Aequor vastum et arcanum est.', 'They sailed across the ocean.', 'Trans aequor navigaverunt.', 'Marine life thrives in the ocean''s depths.', 'Vita marina in profundis aequoris viget.', '{Mare,Pontus}', '{}', 'aequor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (209, 'Hostis', 'Hostis means enemy, a person who is actively opposed or hostile to someone.', 'HOS-tees', 'From Proto-Indo-European *ghostis meaning stranger or guest, implying foreignness.', 'He was captured by the enemy forces.', 'Ab hostium copiis captus est.', 'Enemies in battle show no mercy.', 'Hostes in proelio nullam misericordiam praebent.', 'The two countries were once enemies but are now allies.', 'Duae gentes olim hostes erant, nunc autem socii sunt.', '{Inimicus,Adversarius}', '{}', 'hostis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (210, 'Cura', 'Cura means care or concern, the provision of what is necessary for the health, welfare, and maintenance of someone or something.', 'KOO-rah', 'From Proto-Indo-European *kew- meaning to observe or watch over.', 'Care for others is a sign of compassion.', 'Cura aliorum signum misericordiae est.', 'She took care of the family pet.', 'Animal domesticum familiae curavit.', 'The plant needs regular care to thrive.', 'Planta cura assidua eget ut crescat.', '{Sollicitudo,Tutela}', '{}', 'cura.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (211, 'Vultus', 'Vultus means ''face'' or ''expression,'' referring to the look or appearance of a person or their emotions displayed on the face.', 'WUHL-toos', 'From Latin vultus, likely related to volvo, meaning ''to turn'' or ''to roll''.', 'Her face was calm despite the chaos around her.', 'Vultus eius tranquillus erat quamvis tumultus circumesset.', 'His expression showed clear disappointment.', 'Vultus eius claram deceptionem ostendit.', 'The child’s face lit up with joy.', 'Vultus pueri gaudio eluxit.', '{Facies,Os}', '{}', 'vultus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (212, 'Celer', 'Celer means ''swift'' or ''quick,'' used to describe rapid movement or speed.', 'KAY-ler', 'From Latin celer, akin to Greek keles (quick steed).', 'The swift runner won the race easily.', 'Cursor celer certamen facile vicit.', 'She made a quick decision.', 'Consilium celere cepit.', 'The bird was too swift to be caught.', 'Avis nimis celeris erat quam ut caperetur.', '{Velox,Rapidus}', '{Tardus}', 'celer.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (213, 'Fulgur', 'Fulgur means ''lightning,'' a natural electrical discharge in the atmosphere.', 'FOOL-gur', 'From Latin fulgere, meaning ''to flash'' or ''to shine''.', 'Lightning lit up the night sky.', 'Fulgur caelum nocturnum illuminavit.', 'The storm was accompanied by frequent lightning.', 'Tempestas fulgoribus crebris comitata est.', 'They watched the lightning from a safe distance.', 'Fulgura e loco tuto spectaverunt.', '{Coruscatio,Fulmen}', '{}', 'fulgur.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (214, 'Verbum', 'Verbum means ''word,'' referring to a single distinct meaningful element of speech or writing.', 'WER-boom', 'From Latin verbum, related to Greek epos (word).', 'She chose her words carefully.', 'Verba sua diligenter elegit.', 'Words have the power to heal.', 'Verba vim sanandi habent.', 'The word was unfamiliar to him.', 'Verbum ei ignotum erat.', '{Dictum,Locutio}', '{}', 'verbum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (215, 'Regnum', 'Regnum means ''kingdom,'' representing a realm ruled by a king or queen.', 'REG-noom', 'From Latin rex, meaning ''king''.', 'The kingdom was vast and prosperous.', 'Regnum vastum et opulentum erat.', 'He inherited the kingdom from his father.', 'Regnum a patre accepit.', 'The kingdom faced many challenges.', 'Regnum multis difficultatibus obnoxium erat.', '{Regio,Imperium}', '{}', 'regnum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (216, 'Silvio', 'Silvio refers to the ''forest'' or a wooded area, often associated with natural beauty and tranquility.', 'SEEL-vee-oh', 'From Latin silva, meaning ''forest'' or ''wood''.', 'They hiked through the forest.', 'Per silvam ambulaverunt.', 'The forest was full of wildlife.', 'Silva feris animalibus plena erat.', 'He found peace in the forest.', 'Pacem in silva invenit.', '{Silva,Lucedum}', '{}', 'silvio.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (217, 'Opacus', 'Opacus means ''shady'' or ''dark,'' used to describe areas not reached by sunlight.', 'oh-PAH-koos', 'From Latin opacus, likely derived from ops (help, abundance), indicating abundance of shade.', 'The forest was cool and shady.', 'Silva opaca et frigida erat.', 'They sat in the shade to avoid the sunlight.', 'In umbra sederunt ut solem vitarent.', 'The shady alley was mysterious.', 'Angiportus opacus arcanus erat.', '{Umbrosus,Caliginosus}', '{Lucidus}', 'opacus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (218, 'Sensus', 'Sensus means ''sense'' or ''perception,'' related to the ability to perceive stimuli.', 'SEN-soos', 'From Latin sentire, meaning ''to feel'' or ''to perceive''.', 'Touch is one of the five senses.', 'Tactus unus e quinque sensibus est.', 'She had a strong sense of smell.', 'Sensum odorandi acrem habebat.', 'His sense of taste was dulled.', 'Sensus gustandi eius hebetatus erat.', '{Perceptio,Affectus}', '{}', 'sensus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (219, 'Ala', 'Ala means ''wing,'' referring to the limb or appendage that enables flight in birds and insects.', 'AH-lah', 'From Latin ala, related to Greek ala (wing).', 'The bird extended its wings.', 'Avis alas suas extendit.', 'Wings allow birds to fly.', 'Alae avibus volatum praebent.', 'The insect''s wings fluttered.', 'Alae insecti trepidarunt.', '{Penna,Volatus}', '{}', 'ala.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (220, 'Flora', 'Flora refers to ''plants'' or the plant life occurring in a particular region or period.', 'FLOH-rah', 'Named after Flora, the Roman goddess of flowers.', 'The region is known for its diverse plant life.', 'Regio flora diversa nota est.', 'She studied the flora of the rainforest.', 'Floram silvae pluvialis studuit.', 'The flora of the island is unique.', 'Flora insulae singularis est.', '{Vegetatio,Herbae}', '{}', 'flora.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (221, 'Supernus', 'Supernus means ''heavenly'' or ''divine,'' often used to describe something celestial or elevated.', 'soo-PAIR-noos', 'From Latin superus, meaning ''above''.', 'The sky had a heavenly glow.', 'Caelum supernum lumen habebat.', 'He aspired to reach heavenly heights.', 'Supernas altitudines attingere cupivit.', 'Her voice was of heavenly beauty.', 'Vox eius supernae pulchritudinis erat.', '{Caelestis,Divinus}', '{Terrestris}', 'supernus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (222, 'Fatum', 'Fatum means ''fate'' or ''destiny,'' representing the predetermined course of events.', 'FAH-toom', 'From Latin fari, meaning ''to speak,'' as in what has been spoken by the gods.', 'He believes in destiny.', 'Fato credit.', 'Her fate was sealed.', 'Fatum eius obsignatum erat.', 'The book explores the concept of fate.', 'Liber notionem fati explorat.', '{Destinatio,Sors}', '{}', 'fatum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (223, 'Osculum', 'Osculum means ''kiss,'' referring to a touch or pressing of one''s lips against another person or an object.', 'OS-koo-loom', 'From Latin os, meaning ''mouth''.', 'He gave her a gentle kiss.', 'Osculum lene ei dedit.', 'A kiss can express affection.', 'Osculum amorem exprimere potest.', 'She blew a kiss towards him.', 'Osculum ad eum misit.', '{Suavium,Basium}', '{}', 'osculum.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (224, 'Aequitas', 'Aequitas means ''equity,'' relating to fairness or justice in the way people are treated.', 'EYE-kwee-tahs', 'From Latin aequus, meaning ''equal''.', 'Equity should be upheld in all decisions.', 'Aequitas in omnibus consiliis servanda est.', 'She fought for equity in the workplace.', 'Pro aequitate in loco laboris pugnavit.', 'Equity in law ensures fair treatment.', 'Aequitas in iure iustum tractatum praestat.', '{Justitia,Paritas}', '{Iniquitas}', 'aequitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (225, 'Dignitas', 'Dignitas means ''dignity,'' referring to the state of being worthy of honor or respect.', 'DIG-nee-tahs', 'From Latin dignus, meaning ''worthy''.', 'He maintained his dignity throughout the ordeal.', 'Dignitatem per totam rem adversam servavit.', 'Everyone deserves to be treated with dignity.', 'Omnes cum dignitate tractari merentur.', 'She carried herself with dignity.', 'Se cum dignitate gerebat.', '{Honestas,Decus}', '{Humilitas}', 'dignitas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (226, 'Pulchritudo', 'Pulchritudo means ''beauty,'' often used to describe both physical appearance and moral or spiritual excellence.', 'POOL-kri-too-doh', 'From Latin pulcher, meaning ''beautiful''.', 'The beauty of the sunset was breathtaking.', 'Pulchritudo solis occidentis animum rapiebat.', 'She was admired for her inner beauty.', 'Ob interiorem pulchritudinem admirationi erat.', 'Beauty can be found in unexpected places.', 'Pulchritudo in locis inopinatis inveniri potest.', '{Formositas,Venustas}', '{Turpitudo}', 'pulchritudo.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (227, 'Opinio', 'Opinio means ''opinion,'' representing a belief or judgment that rests on grounds insufficient to produce complete certainty.', 'oh-PI-nee-oh', 'From Latin opinari, meaning ''to think'' or ''to suppose''.', 'In her opinion, the plan was flawed.', 'Eius opinione, consilium vitiosum erat.', 'He expressed his opinion freely.', 'Opinionem suam libere expressit.', 'Opinions can vary widely.', 'Opiniones late variare possunt.', '{Sententia,Cogitatio}', '{}', 'opinio.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (228, 'Effigies', 'Effigies means ''image'' or ''likeness,'' often used to describe a representation of a person.', 'ef-FI-jeez', 'From Latin effingere, meaning ''to form'' or ''to shape''.', 'The statue was an effigy of the ancient ruler.', 'Statua effigies antiqui regis erat.', 'Effigies were used in traditional ceremonies.', 'Effigies in caerimoniis traditiciis adhibebantur.', 'They burned an effigy of their opponent.', 'Effigiem adversarii combusserunt.', '{Imago,Simulacrum}', '{}', 'effigies.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (229, 'Acies', 'Acies means ''sharpness'' or ''edge,'' often used to describe the cutting edge of a weapon or an acute sense.', 'AH-kee-ays', 'From Latin acuere, meaning ''to sharpen''.', 'The blade had a sharp edge.', 'Gladius aciem acutam habebat.', 'His sharpness of mind was extraordinary.', 'Acies mentis eius extraordinaria erat.', 'The acies of the sword was formidable.', 'Acies gladii formidabilis erat.', '{Acutus,Exacuo}', '{}', 'acies.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (230, 'Aes', 'Aes means ''bronze'' or ''money,'' historically used to describe both the metal and Roman currency.', 'AYS', 'From Latin aes, referring to copper and its alloys.', 'The statue was made of bronze.', 'Statua ex aere facta erat.', 'They traded in bronze coins.', 'Nummis aeneis mercaturam faciebant.', 'Bronze was a valuable resource.', 'Aes res pretiosa erat.', '{Cuprum,Metallum}', '{}', 'aes.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (231, 'Ager', 'Ager means ''field'' or ''land'' in Latin.', 'AH-ger', 'From Proto-Indo-European *h₂éǵros, meaning ''field''.', 'The farmer plowed the field.', 'Agricola agrum aravit.', 'They walked across the open land.', 'Per agrum apertum ambulaverunt.', 'The crops took root in the fertile field.', 'Fruges in agro fertili radices egerunt.', '{Arvum,Pratum}', '{}', 'ager.jpg');
