INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (250, 'Voluntas', 'Voluntas means will or desire.', 'vo-LUN-tas', 'From the Latin word ''velle'', meaning ''to wish''.', 'His voluntas to succeed was unparalleled.', 'Voluntas eius ad rem gerendam sine pari erat.', 'She followed her own voluntas in life.', 'Suam ipsam voluntatem in vita secuta est.', 'Voluntas of the people is reflected in their leaders.', 'Voluntas populi in ducibus eorum cernitur.', '{Desiderium,Cupido}', '{}', 'voluntas.jpg');
