INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (263, 'Albus', 'Albus means the color white, often associated with purity and innocence.', 'ˈal.bus', 'From Proto-Indo-European *h₂elbʰ- (white).', 'The snow was albus, covering the entire landscape.', 'Nix alba erat, totum agrum tegens.', 'She wore an albus dress to the wedding.', 'Vestem albam ad nuptias gerebat.', 'The swan''s feathers are strikingly albus.', 'Plumae cygni insigniter albae sunt.', '{Candidus,Niveus}', '{Niger}', 'albus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (264, 'Laetitia', 'Laetitia means joy or happiness, often depicting a state of bliss or delight.', 'laːˈti.ti.a', 'Derived from Latin laetus, meaning ''glad, happy''.', 'Their hearts were filled with laetitia at the news.', 'Corda eorum laetitia ad nuntium impleta sunt.', 'The festival was a time of great laetitia.', 'Festum tempus magnae laetitiae erat.', 'She expressed her laetitia with a broad smile.', 'Laetitiam suam lato risu expressit.', '{Gaudium,Felicitas}', '{Tristitia}', 'laetitia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (265, 'Tristitia', 'Tristitia means sadness or sorrow, often used to convey a sense of melancholy or unhappiness.', 'trisˈtiː.ti.a', 'From Latin tristis, meaning ''sad''.', 'The poem was full of tristitia and longing.', 'Carmen tristitia et desiderio plenum erat.', 'He couldn''t hide his tristitia after the loss.', 'Tristitiam suam post damnum celare non poterat.', 'The gray skies mirrored her tristitia.', 'Caelum nubilum tristitiam eius referebat.', '{Maeror,Dolor}', '{Laetitia}', 'tristitia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (266, 'Sagax', 'Sagax means wise or perceptive, indicating a keen and insightful mind.', 'ˈsa.ɡaks', 'From Latin sagus, meaning ''prophetic, wise''.', 'The sagax philosopher offered profound insights.', 'Philosophus sagax sententias profundas protulit.', 'Her sagax decisions led to success.', 'Consilia eius sagacia ad prosperum exitum duxerunt.', 'A sagax leader anticipates challenges.', 'Dux sagax difficultates praevidet.', '{Prudens,Intelligens}', '{Inscius}', 'sagax.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (267, 'Amicitia', 'Amicitia means friendship, a bond of mutual affection and support.', 'a.miˈki.ti.a', 'Derived from Latin amicus, meaning ''friend''.', 'Their amicitia lasted a lifetime.', 'Amicitia eorum per totam vitam duravit.', 'She cherished their amicitia dearly.', 'Amicitiam eorum valde diligebat.', 'Amicitia is a valuable treasure.', 'Amicitia thesaurus pretiosus est.', '{Concordia,Societas}', '{Hostilitas}', 'amicitia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (268, 'Noxius', 'Noxius means harmful or injurious, often used to describe something that causes damage or pain.', 'ˈno.ksi.us', 'From Latin noxa, meaning ''harm''.', 'Some chemicals are noxius to health.', 'Quaedam chemica noxia valetudini sunt.', 'Avoid noxius influences in life.', 'Noxias res in vita evita.', 'The noxius fumes filled the air.', 'Fumi noxii aerem impleverunt.', '{Perniciosus,Maleficus}', '{Innocuus}', 'noxius.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (269, 'Magnus', 'Magnus means great or large, often used to denote size, importance, or power.', 'ˈmaŋ.nus', 'From Proto-Indo-European *meg- (great).', 'He was a magnus figure in history.', 'Vir magnus in historia fuit.', 'The magnus mountain loomed before us.', 'Mons magnus ante nos imminebat.', 'His magnus achievements were celebrated.', 'Magna facta eius celebrabantur.', '{Grandis,Amplus}', '{Parvus}', 'magnus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (270, 'Acerbus', 'Acerbus means bitter or harsh, often used to describe taste or experiences.', 'ˈa.ker.bus', 'From Proto-Indo-European *h₂eḱer- (sharp, bitter).', 'The acerbus taste was difficult to bear.', 'Sapor acerbus toleratu difficilis erat.', 'He endured acerbus criticism with grace.', 'Acerbam reprehensionem cum gratia pertulit.', 'The acerbus winter was finally over.', 'Hiems acerba tandem praeteriit.', '{Amarus,Austerus}', '{Dulcis}', 'acerbus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (271, 'Vir', 'Vir means man, often used to denote an adult male or a person with male characteristics.', 'wiːr', 'From Proto-Indo-European *wiHrós, meaning ''man''.', 'He is a vir of great honor.', 'Vir magnae honoris est.', 'The vir stood tall and proud.', 'Vir rectus et superbus stabat.', 'In times of old, a brave vir led the tribe.', 'Antiquis temporibus, vir fortis tribum duxit.', '{Homo,Mas}', '{Femina}', 'vir.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (272, 'Fidelis', 'Fidelis means faithful or loyal, typically describing allegiance or steadfastness.', 'fiˈdeː.lis', 'From Latin fides, meaning ''faith''.', 'A fidelis friend is a treasure.', 'Amicus fidelis thesaurus est.', 'The dog was known for being fidelis.', 'Canis fidelis esse notus erat.', 'Her fidelis service was honored.', 'Fidele servitium eius honoratum est.', '{Loyalis,Constans}', '{Infidelis}', 'fidelis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (273, 'Mulier', 'Mulier means woman, often denoting an adult female human.', 'ˈmu.li.er', 'From Proto-Indo-European *mulyeh₂, meaning ''woman''.', 'The mulier spoke with wisdom and grace.', 'Mulier cum sapientia et gratia locuta est.', 'She was a strong mulier in her community.', 'Mulier fortis in communitate sua erat.', 'The mulier wore a beautiful gown.', 'Mulier pulchram vestem gerebat.', '{Femina,Matrona}', '{Vir}', 'mulier.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (274, 'Calor', 'Calor means heat or warmth, typically used to describe temperature or emotional warmth.', 'ˈka.lor', 'Derived from Latin calere, meaning ''to be warm''.', 'The calor of the sun was intense.', 'Calor solis vehemens erat.', 'She felt the calor of the fireplace.', 'Calorem camini sensit.', 'The calor of summer was finally here.', 'Calor aestatis tandem aderat.', '{Aestus,Fervor}', '{Frigus}', 'calor.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (275, 'Claritas', 'Claritas means clarity or brightness, often used to signify clear understanding or shining light.', 'ˈkla.ri.taːs', 'From Latin clarus, meaning ''clear, bright''.', 'The claritas of the message was appreciated.', 'Claritas nuntii grata erat.', 'She admired the claritas of the morning sky.', 'Claritatem caeli matutini admirata est.', 'The claritas of his argument was compelling.', 'Claritas argumenti eius firma erat.', '{Luce,Puritas}', '{Obscuritas}', 'claritas.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (276, 'Dulcis', 'Dulcis means sweet, often referring to taste, sound, or affection.', 'ˈdul.kis', 'From Proto-Indo-European *dl̥kú-, meaning ''sweet''.', 'The fruit was dulcis and satisfying.', 'Fructus dulcis et gratus erat.', 'Her voice had a dulcis tone.', 'Vox eius dulcem sonum habebat.', 'Sweet memories bring dulcis feelings.', 'Dulces memoriae dulces sensus afferunt.', '{Suavis,Lenis}', '{Acerbus}', 'dulcis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (277, 'Sanguis', 'Sanguis means blood, often used to refer to the lifeblood or kinship.', 'ˈsaŋ.ɡwis', 'From Proto-Indo-European *h₁sh₂-én-gʷʰs, meaning ''blood''.', 'The sanguis ties between them were unbreakable.', 'Vincula sanguinis inter eos insolubilia erant.', 'Sanguis flowed from the wound.', 'Sanguis e vulnere fluxit.', 'He had the sanguis of royalty.', 'Sanguinem regium habebat.', '{Cruor,Vita}', '{}', 'sanguis.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (278, 'Aquarius', 'Aquarius means the water-bearer, typically a constellation and zodiac sign.', 'ah-KWAH-ree-oos', 'Derived from Latin ''aqua'' meaning water.', 'Aquarius is the eleventh astrological sign.', 'Aquarius undecimum signum astrologicum est.', 'The constellation of Aquarius is visible in the night sky.', 'Sidus Aquarii in caelo nocturno conspicitur.', 'Many people born in late January and early February are Aquarians.', 'Multi homines extremo Ianuario et primo Februario nati sub Aquario sunt.', '{Aquarius,Aqua-portator}', '{}', 'aquarius.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (279, 'Saccus', 'Saccus means a bag or sack, a container used to carry items.', 'SAK-koos', 'Derived from Greek ''sakkos'', meaning a sack.', 'The traveler filled his bag with clothes.', 'Viator saccum suum vestimentis implevit.', 'She carried a small bag to the market.', 'Parvum saccum ad mercatum portavit.', 'The bag was made of leather.', 'Saccus e corio factus erat.', '{Follis,Crumena}', '{}', 'bag.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (280, 'Lectus', 'Lectus means a bed, a piece of furniture for sleeping or resting.', 'LEK-toos', 'From Latin ''lectus'', meaning bed or couch.', 'She lay on the bed and read a book.', 'In lecto iacuit et librum legit.', 'The bed was very comfortable.', 'Lectus valde commodus erat.', 'He made the bed after waking up.', 'Lectum post somnum stravit.', '{Cubile,Stratum}', '{}', 'bed.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (281, 'Niger', 'Niger means black, a color that is the darkest shade.', 'NEE-ger', 'From Latin ''niger'', meaning black or dark.', 'He wore a black coat.', 'Pallium nigrum gerebat.', 'The night sky was black.', 'Caelum nocturnum nigrum erat.', 'Black ink was used for writing.', 'Atramentum nigrum ad scribendum adhibitum est.', '{Ater,Fuscus}', '{Albus}', 'black.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (284, 'Sella', 'Sella means a chair, a piece of furniture for sitting.', 'SEL-la', 'From Latin ''sella'', meaning seat or chair.', 'He sat on the chair by the desk.', 'In sella iuxta mensam sedebat.', 'The chair was made of wood.', 'Sella e ligno facta erat.', 'She pulled out a chair for her guest.', 'Sellam hospiti suo protraxit.', '{Cathedra,Subsellium}', '{}', 'chair.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (285, 'Arca', 'Arca means a chest, a large strong box for storage.', 'AR-ka', 'From Latin ''arca'', meaning box or chest.', 'He stored his valuables in a chest.', 'Res pretiosas in arca condidit.', 'The chest was made of oak.', 'Arca e quercu facta erat.', 'She opened the chest to find her old clothes.', 'Arcam aperuit ut veteres vestes inveniret.', '{Cista,Capsa}', '{}', 'chest.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (286, 'Vestimenta', 'Vestimenta means clothes, garments worn on the body.', 'wes-TEE-men-ta', 'From Latin ''vestis'', meaning garment.', 'She bought new clothes for the party.', 'Nova vestimenta ad convivium emit.', 'His clothes were neatly folded.', 'Vestimenta eius nitide complicata erant.', 'The clothes were drying on the line.', 'Vestimenta in linea siccabantur.', '{Vestis,Amictus}', '{}', 'clothes.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (287, 'Nubis', 'Nubis means a cloud, a visible mass of condensed water vapor in the atmosphere.', 'NOO-bis', 'From Latin ''nubes'', meaning cloud.', 'The sky was filled with clouds.', 'Caelum nubibus plenum erat.', 'A dark cloud covered the sun.', 'Nubis obscura solem texit.', 'The clouds moved quickly across the sky.', 'Nubes celeriter per caelum movebantur.', '{Nubes,Nebula}', '{}', 'cloud.jpg');
