-- Flyway-style migration: creates the word_of_the_day table
-- and seeds it with all Latin vocabulary words.
-- =============================================================

CREATE TABLE IF NOT EXISTS word_of_the_day (
    id SERIAL PRIMARY KEY,
    word VARCHAR(255) NOT NULL,
    definition TEXT NOT NULL,
    pronunciation VARCHAR(255),
    origin VARCHAR(255),
    example0 TEXT,
    example0_latin TEXT,
    example1 TEXT,
    example1_latin TEXT,
    example2 TEXT,
    example2_latin TEXT,
    synonyms TEXT[],
    antonyms TEXT[],
    image VARCHAR(255)
);

-- Seed data (285 words)

-- Reset sequence to match max ID
SELECT setval('word_of_the_day_id_seq', (SELECT MAX(id) FROM word_of_the_day));

-- Verify row count
SELECT count(*) AS total_words FROM word_of_the_day;
