INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (392, 'Ignorantia', 'Ignorantia means a lack of knowledge, understanding, or awareness about something.', 'iɡ-no-ran-ti-a', 'Derived from the Latin verb ''ignorare'', meaning ''to not know''.', 'Ignorance can often lead people to make uninformed decisions.', NULL, 'The scientist worked to dispel ignorance through education.', NULL, 'Ignorance of the law is not a valid excuse.', NULL, '{nescientia,inscientia}', '{scientia,cognitio}', 'ignorantia.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (393, 'Libri', 'Libri means books or written documents, typically bound and containing written or printed material.', 'li-bri', 'Comes from the Latin noun ''liber'', meaning ''book''.', 'The library was filled with old books on philosophy and history.', NULL, 'She spent her evening reading books by her favorite authors.', NULL, 'Books hold the key to unlocking new worlds and ideas.', NULL, '{volumina,codices}', '{}', 'libri.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (394, 'Auris', 'Auris means ear, the organ of hearing and balance in humans and other animals.', 'aw-ris', 'From Latin ''auris'', related to the Greek word ''ούς'' (ous) meaning ''ear''.', 'She whispered a secret into his ear.', NULL, 'The loud noise hurt his ears.', NULL, 'He listens carefully with his ears during the lecture.', NULL, '{auditio,auditus}', '{}', 'auris.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (395, 'Flamma', 'Flamma means flame, referring to the visible, gaseous part of a fire that emits light.', 'flam-ma', 'From the Latin root ''flamma'', which is akin to the Greek ''φλόξ'' (phlox), meaning flame or blaze.', 'The flame flickered in the breeze.', NULL, 'They sat by the campfire, watching the flames dance.', NULL, 'The flames consumed the dry wood quickly.', NULL, '{ardor,ignis}', '{gelum}', 'flamma.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (396, 'Avaritia', 'Avaritia means ''greed'' or ''avarice'' in Latin. It refers to an excessive or insatiable desire for wealth or material gain.', 'ah-vah-REE-tee-ah', 'Derived from the Latin adjective ''avarus'', meaning ''greedy''.', 'Greed can lead to one''s downfall.', NULL, 'The king''s avarice knew no bounds.', NULL, 'Avarice is one of the seven deadly sins.', NULL, '{Cupiditas,Rapacitas}', '{Liberalitas,Humilitas}', 'avaritia.jpg');
