INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (404, 'Glacies', 'Glacies means ice, the solid state of water that forms at low temperatures.', 'GLAH-kee-ays', 'From Proto-Italic *glakiās, from Proto-Indo-European *gel- (to freeze).', 'The lake was covered in a thick layer of ice.', NULL, 'Ice can be slippery and dangerous.', NULL, 'In winter, we often see ice forming on window panes.', NULL, '{Gelus,Pruna}', '{Ignis}', 'glacies.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (405, 'Puer', 'Puer means boy, referring to a male child or young man.', 'POO-er', 'From Proto-Italic *pouros, from Proto-Indo-European *ph₂wēr (child, offspring).', 'The boy plays in the park.', NULL, 'He is a curious boy, always asking questions.', NULL, 'The boy is learning to read and write.', NULL, '{Infans,Puerulus}', '{Puella}', 'puer.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (406, 'Fluvius', 'Fluvius means river, a large natural stream of water flowing to the sea, a lake, or another such stream.', 'FLU-vee-us', 'From Proto-Italic *flowyos, from Proto-Indo-European *plew- (to flow).', 'The river flows through the valley.', NULL, 'Many cities are located on the banks of a river.', NULL, 'The river is home to many species of fish.', NULL, '{Amnis,Rivus}', '{}', 'fluvius.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (407, 'Homines', 'Homines means people, referring to human beings collectively.', 'HOH-mee-nes', 'From Latin homo (man, human), plural form homines.', 'People need clean water to survive.', NULL, 'The city was bustling with people.', NULL, 'People of all ages attended the event.', NULL, '{Gens,Populus}', '{}', 'homines.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (408, 'Ludus', 'Ludus means game or play, referring to an activity for amusement or entertainment.', 'LOO-doos', 'From Proto-Italic *loudus, from Proto-Indo-European *leygʰ- (to jump).', 'They played a game of chess.', NULL, 'The children are outside playing games.', NULL, 'Board games are a popular pastime.', NULL, '{Jocus,Certamen}', '{Labor}', 'ludus.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (409, 'Agricola', 'Agricola means farmer, a person engaged in agriculture, raising living organisms for food or raw materials.', 'a-GREE-ko-la', 'From ager (field) + colō (cultivate), literally ''cultivator of fields''.', 'The farmer plowed his fields in spring.', NULL, 'Farmers work hard to produce food.', NULL, 'She inherited the farm from her father, a devoted farmer.', NULL, '{Rusticus,Colonus}', '{}', 'agricola.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (410, 'Nomen', 'Nomen means name, a word or set of words by which a person or thing is known, addressed, or referred to.', 'NOH-men', 'From Proto-Italic *nōmn, from Proto-Indo-European *h₁nómn̥.', 'Her name is Julia.', NULL, 'Names can hold significant meaning.', NULL, 'They wrote their names on the list.', NULL, '{Appellatio,Titulus}', '{}', 'nomen.jpg');
INSERT INTO word_of_the_day (id, word, definition, pronunciation, origin, example0, example0_latin, example1, example1_latin, example2, example2_latin, synonyms, antonyms, image) VALUES (411, 'Somnium', 'Somnium means dream, a series of thoughts, images, and sensations occurring in a person''s mind during sleep.', 'SOM-nee-um', 'From somnus (sleep) + -ium (noun suffix).', 'She recounted her strange dream to her friends.', NULL, 'Dreams can sometimes be difficult to interpret.', NULL, 'He had a recurring dream about flying.', NULL, '{Visio,Insomnia}', '{}', 'somnium.jpg');
